INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Arapça', '2', '1. ÜNİTE: HAYDİ TANIŞALIM', 'ARP.2.1.1. DİNLEME/İZLEME-ANLAMLANDIRMA', 'Arapça selamlaşma ve tanışma ifadeleriyle ilgili (ال  ﱠسﻼُم َعَلْيُكم، َمْرَحًبا vb.) içerik hazırlanır.', 'Video', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Arapça', '2', '1. ÜNİTE: HAYDİ TANIŞALIM', 'ARP.2.1.1. DİNLEME/İZLEME-ANLAMLANDIRMA', '(أ، ب، ت، ث، ج، ح، خ) Arapça harflerinin yazılış biçimlerinin doğru kalem hareketleri ve yazı yönü gösterilerek satır üzerinde canlandırıldığı içerik hazırlanır.', 'Video', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Arapça', '2', '1. ÜNİTE: HAYDİ TANIŞALIM', 'ARP.2.1.6. YAZMA-ANLATMA', 'Öğrencinin ekrandaki yazı modelini noktalı izleri ve kalem hareket sırasını takip ederek satır çizgisi üzerinde harfleri kopyaladığı, görsel ve sesli dönüt veren bir yazı pratiği içeriği hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Arapça', '2', '1. ÜNİTE: HAYDİ TANIŞALIM', 'ARP.2.1.1. DİNLEME/İZLEME-ANLAMLANDIRMA', 'Arapça selamlaşma ve tanışma ifadeleriyle ilgili (َصباُح الَخْير، َمساُء الَخْير، َتَش ﱠرْفُت vb.) içerik hazırlanır.', 'Video', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Arapça', '2', '1. ÜNİTE: HAYDİ TANIŞALIM', 'ARP.2.1.6. YAZMA-ANLATMA', 'Selamlaşma ve tanışmayla ilgili boşluk doldurma, eşleştirme, dinlediği ifadeye uygun görsel seçme, hafıza kartı oyunu vb. çeşitli etkinliklerin olduğu içerik bir hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Arapça', '2', '1. ÜNİTE: HAYDİ TANIŞALIM', 'ARP.2.1.2. DİL BİLGİSİ SEÇME-KULLANMA', 'ِاْسمي ...، ما اْسُمَك/ِك؟ / َكْيَف حاُلَك/ِك؟، َأنا ِبَخْير                                                         İfadeleriyle ilgili telaffuzların ağız hareketleriyle desteklendiği içerik hazırlanır.', 'Video', NULL);
