INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Arapça', '5', '3. ÜNİTE: AİLEM', 'ARP.5.3.6. YAZMA-ANLATMA', 'ُمَعِّلم/ ُمَعِّلَمة، عاِمل/ عاِمَلة، ُمَهْنِدس/ ُمَهْنِدَسة vb. mesleklerle ilgili boşluk doldurma, eşleştirme, dinlediği ifadeye uygun görsel seçme, hafıza kartı oyunu vb. çeşitli etkinliklerin olduğu içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Arapça', '5', '3. ÜNİTE: AİLEM', 'ARP.5.3.3. DİL BİLGİSİ SEÇME-KULLANMA', '… َمْن ُهَو؟ َمْن ِهَي؟ ُهَو أبي/ُمَعِّلم، ِهَي ُأِّمي/ُمَعِّلَمة ...ما اْسُمُه/ها؟، ِاْسُمُه/ها ...، ما ِمْهَنُتُه/ها؟، ُهَو/ِهَي/َأبي/ُأّمي
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Arapça', '5', '3. ÜNİTE: AİLEM', 'ARP.5.3.6. YAZMA-ANLATMA', 'َمْن ُهَﻮ؟ َمْن ِهَي؟ ُهَﻮ أبي/ُمَعِّلم، ِهَي ُأِّمي/ُمَعِّلَمة ...ما اْسُمُه/ها؟، ِاْسُمُه/ها ...، ما ِمْهَنُتُه/ها؟، ُهَﻮ/ِهَي/َأبي/ُأّمي …
