INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Arapça', '9', '1. ÜNİTE: HAYDİ TANIŞALIM', 'ARP.9.1.1. DİNLEME/İZLEME-ANLAMLANDIRMA', 'Bir kız ve bir erkek öğrencinin okulun ilk gününde tanışmasını konu alan ve temel selamlaşma ifadelerinin geçtiği, telaffuzların ağız hareketleriyle desteklendiği içerik hazırlanır.', 'Video', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Arapça', '9', '1. ÜNİTE: HAYDİ TANIŞALIM', 'ARP.9.1.1. DİNLEME/İZLEME-ANLAMLANDIRMA', 'Arapça hedef harflerin başta, ortada ve sonda yazılış biçimlerinin kalem hareketleri ve yazı yönü gösterilerek satır üzerinde canlandırıldığı içerik hazırlanır.', 'Video', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Arapça', '9', '1. ÜNİTE: HAYDİ TANIŞALIM', 'ARP.9.1.7. YAZMA-ANLATMA', 'Öğrencinin ekrandaki yazı modelini noktalı izleri ve kalem hareket sırasını takip ederek satır çizgisi üzerinde harf ve kelimeleri kopyaladığı, görsel ve sesli dönüt veren içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
