INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Arapça', '9', '4. ÜNİTE: BİR GÜNÜM', 'ARP.9.4.1. DİNLEME/İZLEME-ANLAMLANDIRMA', 'Yiyecek ve içeceklerle ilgili içeri hazırlanır.', 'Video', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Arapça', '9', '4. ÜNİTE: BİR GÜNÜM', 'ARP.9.4.6. YAZMA-ANLATMA', 'Yiyecek ve içeceklerle ilgili boşluk doldurma, eşleştirme, dinlediği ifadeye uygun görsel seçme, hafıza kartı oyunu vb. çeşitli etkinliklerin olduğu içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Arapça', '9', '4. ÜNİTE: BİR GÜNÜM', 'ARP.9.4.1. DİNLEME/İZLEME-ANLAMLANDIRMA', 'Restoranda yemek siparişi veren bir çocuğun günlük hayattan konuşmasıyla ilgili içerik hazırlanır.', 'Video', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Arapça', '9', '4. ÜNİTE: BİR GÜNÜM', 'ARP.9.4.6. YAZMA-ANLATMA', 'Öğrencinin boşlukların olduğu bir metni anlamlı ifadelerle tamamladığı içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '4', 'GÜNLÜK HAYATTAKİ DİNÎ İFADELER', '4.1.1. Dinî ifadeleri, günlük konuşmalarda doğru ve yerinde kullanır.', '* Günlük konuşmalarda dinî ifadelerin kullanımını içeren bir senaryo üzerinden video veya animasyon hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '4', 'GÜNLÜK HAYATTAKİ DİNÎ İFADELER', '4.1.2. Tekbir ve salavatı söyler.', '* Tekbir ve salavatın okunuşunu içeren bir ses dosyası hazırlanır ve metin üzerinde okunan bölüm eş zamanlı olarak gösterilir.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '4', 'GÜNLÜK HAYATTAKİ DİNÎ İFADELER', '4.1.4. Sübhaneke duasını okur, anlamını söyler.', '* Sübhaneke duasının Arapça okunuşunu içeren bir ses dosyası hazırlanır ve metin üzerinde okunan bölüm eş zamanlı olarak gösterilir.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '4', 'İSLAM’I TANIYALIM', '4.2.3. Kur’an-ı Kerim’in iç düzeni ile ilgili kavramları tanımlar.', '* Kur’an-ı Kerim’in bölümlerinin tanıtıldığı bir video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '4', 'İSLAM’I TANIYALIM', '4.2.4. Âmentü duasını okur, anlamını söyler.', '* Âmentü duasının Arapça okunuşunu içeren bir ses dosyası hazırlanır ve metin üzerinde okunan bölüm eş zamanlı olarak gösterilir.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '4', 'GÜZEL AHLAK', '4.3.3. Fâtiha suresini okur, anlamını söyler.', '* Fâtiha suresinin Arapça okunuşunu içeren bir ses dosyası hazırlanır ve metin üzerinde okunan bölüm eş zamanlı olarak gösterilir.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '4', 'HZ. MUHAMMED’İ TANIYALIM', '4.4.2. Hz. Muhammed’in (s.a.v.) aile büyüklerini tanır.', '* Hz. Peygamber’in aile bireylerinin tanınmasına yönelik bir sürükle bırak etkinliği etkileşimli içerik olarak hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '4', 'HZ. MUHAMMED’İ TANIYALIM', '4.4.3. Hz. Muhammed’in (s.a.v.) doğumu, çocukluk ve gençlik yıllarını özetler.', '* Hz. Peygamber’in çocukluk ve gençlik yıllarını zaman çizelgesi üzerinde özetleyen bir bilgi grafiği veya etkileşimli içerik hazırlanır.', 'İnfografik/Etkileşi mli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '4', 'HZ. MUHAMMED’İ TANIYALIM', '4.4.5. Hz. Muhammed’in (s.a.v.) Mekke ve Medine yıllarını özetler.', '* Hz. Peygamber’in Mekke ve Medine yıllarını zaman çizelgesi üzerinde özetleyen bir bilgi grafiği veya etkileşimli içerik hazırlanır.', 'İnfografik/Etkileşi mli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '4', 'HZ. MUHAMMED’İ TANIYALIM', '4.4.6. Salli ve Barik dualarını okur, anlamını söyler.', '* Salli ve Barik dualarının Arapça okunuşunu içeren bir ses dosyası hazırlanır  ve metin üzerinde okunan bölüm eş zamanlı olarak gösterilir.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '4', 'DİN VE TEMİZLİK', '4.5.1. İslam dininin temizliğe verdiği öneme örnekler verir.', '* İslam dinindeki temizlik ile ilgili kavramlar ve bu kavramların anlamlarına yönelik etkileşimli içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '5', '1. ALLAH İNANCI', 'Köprü Kurma', 'Fen bilimleri dersinde öğrenilen tabiat olaylarındaki (Dünya’nın hem kendi hem de Güneş etrafında dönüşü, mevsimlerin oluşumu, su döngüsü vb.) doğa yasaları ile Allah’ın (cc) yaratıcılığı arasında ilişki kuran video veya etkileşimli video hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '5', '1. ALLAH İNANCI', 'DKAB.5.1.2. Evrendeki mükemmel düzeni gözlem yoluyla fark edip Allah’ın (cc) varlığı ve birliğini gözleme dayalı tahmin edebilme', 'Tevhit kavramıyla ilgili ayetlerden ve hadislerden öğrenci seviyesine uygun örnekler içeren video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '5', '1. ALLAH İNANCI', 'DKAB.5.1.3. Allah’ın (cc) güzel isimleri hakkında bilgi toplayabilme', 'Allah’ın (cc) güzel isimleri hakkında video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '5', '1. ALLAH İNANCI', 'DKAB.5.1.4. İhlas suresini ve bu surenin anlamını okuyarak yorumlayabilme', 'İhlas suresinin Arapça metni verilerek her bir ayetin veya tamamının üzerine tıklandığında okunuşunun dinlenebildiği ve metin üzerinde eş zamanlı olarak vurgulandığı ve anlamının görüntülenebildiği etkileşimli içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '5', '2. NAMAZ', 'DKAB.5.2.1. Namaz ibadetini özetleyebilme', 'Namaz çeşitleri, rekât sayıları, vakitleri ile ilgili etkileşimli içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '5', '2. NAMAZ', 'DKAB.5.2.2. Namazın kılınışını gözlemleyebilme', 'Abdestin alınış sırasını gösteren video veya etkileşimli içerik hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '5', '2. NAMAZ', 'DKAB.5.2.4. Tahiyyat duasını ve bu duanın anlamını okuyarak yorumlayabilme', 'Tahiyyat duasının Arapça metni verilerek her bir ayetin veya tamamının üzerine tıklandığında okunuşunun dinlenebildiği ve metin üzerinde eş zamanlı olarak vurgulandığı ve anlamının görüntülenebildiği etkileşimli içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '5', '3. KUR’AN-I KERİM', 'DKAB.5.3.1. Kur’an-ı Kerim’in iç düzenini
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '5', '3. KUR’AN-I KERİM', 'DKAB.5.3.2. Kur’an-ı Kerim’in temel özellikleri hakkında bilgi toplayabilme', 'Kur’an-ı Kerim’in her bir temel özelliği ile ilgili üzerine tıklandığında mealleri görülebilen ayet ve hadisler içeren etkileşimli içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '5', '3. KUR’AN-I KERİM', 'DKAB.5.3.4. Kevser suresini ve bu surenin anlamını okuyarak yorumlayabilme', 'Kevser suresinin Arapça metni verilerek her bir ayetin veya tamamının üzerine tıklandığında okunuşunun dinlenebildiği ve metin üzerinde eş zamanlı olarak vurgulandığı ve anlamının görüntülenebildiği etkileşimli içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '5', '4. PEYGAMBER KISSALARI', 'DKAB.5.4.2. Peygamber kıssalarında verilen öğütleri sentezleyebilme', 'Hz. Nuh kıssası Yunus, Hud ve Nuh surelerindeki ilgili ayetler kapsamında; Hz. İbrahim kıssası Enbiya suresi çerçevesinde; Hz. Musa kıssası Araf, Taha ve Kasas surelerindeki ayetler bağlamında; Hz. İsa kıssası Al-i İmran, Maide ve Meryem surelerindeki ilgili ayetlerle ilişkilendirilerek bu kıssalarla ilgili video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '5', '4. PEYGAMBER KISSALARI', 'DKAB.5.4.3. Kureyş suresini ve bu surenin anlamını okuyarak yorumlayabilme', 'Kureyş suresinin Arapça metni verilerek her bir ayetin veya tamamının üzerine tıklandığında okunuşunun dinlenebildiği ve metin üzerinde eş zamanlı olarak vurgulandığı ve anlamının görüntülenebildiği etkileşimli içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '5', '5. MİMARİMİZDE DİNÎ MOTİFLER', 'DKAB.5.5.1. Dinin mimarimize etkisini çözümleyebilme', 'Çeşitli bölgelerde bulunan cami, çeşme, medrese, külliye, kervansaray gibi mimari eserleri ve bu eserlerde kullanılan hat, tezhip, çini, minyatür gibi sanatları gösteren video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '5', '5. MİMARİMİZDE DİNÎ MOTİFLER', 'DKAB.5.5.2. Camilerin bölümlerini tanıyabilme', 'Mihrap, minber, vaaz kürsüsü, minare, şerefe, şadırvan, kubbe gibi caminin iç ve dış bölümlerinin gözlemlenmesine ve her bölüm hakkında bilgi almalarına olanak sağlayan sanal gezi hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '5', '5. MİMARİMİZDE DİNÎ MOTİFLER', 'DKAB.5.5.3. Kültürümüzde yer alan cami örneklerini karşılaştırabilme', 'Hat, ebru, çini, taş ve ahşap oymacılığı gibi sanatlarla ilgili örneklerin yer aldığı video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '6', '1. PEYGAMBER VE İLAHİ KİTAP
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '6', '1. PEYGAMBER VE İLAHİ KİTAP İNANCI', 'DKAB.6.1.3. İlahi kitaplarla ilgili bilgileri çözümleyebilme.', 'İlahi kitapların özelliklerini ve içerdikleri mesajları özetleyen bir video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '6', '1. PEYGAMBER VE İLAHİ KİTAP İNANCI', 'DKAB.6.1.4. Felak suresini ve bu surenin anlamını okuyarak yorumlayabilme.', 'Felak suresini ve anlamını anlatan, suredeki duaların önemini vurgulayan bir video hazırlanır. Surenin doğru telaffuzunu içeren ses dosyası hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '6', '2. RAMAZAN VE ORUÇ', 'DKAB.6.2.1. Ramazan ayının oruç ve Kur’an ayı olduğunu çözümleyebilme.', 'Ramazan ayının manevi anlamı ve oruç ibadetinin önemine yönelik içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '6', '2. RAMAZAN VE ORUÇ', 'DKAB.6.2.2. Oruç ibadeti hakkında bilgi toplayabilme.', 'Oruç ibadetinin dini hükümleri ve çeşitlerine (farz, vacip, sünnet) yönelik içerik hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '6', '2. RAMAZAN VE ORUÇ', 'DKAB.6.2.3. Oruç ibadetinin insana kazandırdıklarını yapılandırabilme.', 'Oruç tutmanın insana kazandırdığı manevi ve ahlaki değerleri anlatan bir video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '6', '2. RAMAZAN VE ORUÇ', 'DKAB.6.2.4. İftar duasını ve bu duanın anlamını okuyarak yorumlayabilme.', 'İftar duasının anlamını ve nasıl okunduğunu anlatan bir video, ses dosyası hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '6', '3. AHLAKİ DAVRANIŞLAR', 'DKAB.6.3.1. Doğru sözlü olmayı kendi hayatına yansıtabilme.', 'Doğru sözlü olmanın İslam''daki yeri ve hayatımızdaki önemi hakkında bilgi veren bir video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '6', '3. AHLAKİ DAVRANIŞLAR', 'DKAB.6.3.2. Merhametli olmayı yorumlayabilme.', 'Merhametin İslam’da ve toplumda nasıl bir yer tuttuğunu anlatan bir video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '6', '3. AHLAKİ DAVRANIŞLAR', 'DKAB.6.3.3. Adap ve nezaket kurallarını özetleyebilme.', 'İslam’daki adap ve nezaket kurallarını açıklayan bir video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '6', '3. AHLAKİ DAVRANIŞLAR', 'DKAB.6.3.4. Vatanını sevmenin önemi hakkında çıkarım yapabilme.', 'Vatan sevgisinin önemine yönelik içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '6', '3. AHLAKİ DAVRANIŞLAR', 'DKAB.6.3.5. Kunut dualarını ve bu duaların anlamlarını okuyarak yorumlayabilme.', 'Kunut dualarının anlamını ve nasıl okunduğunu anlatan bir video, ses dosyası hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '6', '4. PEYGAMBERLİĞİNDEN ÖNCE HZ. MUHAMMED', 'DKAB.6.4.1. Hz. Muhammed’in (sav) doğduğu çevreyi tarihsel bağlamda değerlendirebilme.', 'Hz. Muhammed’in doğduğu Mekke’nin tarihsel ve coğrafi koşullarını anlatan bir video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '6', '4. PEYGAMBERLİĞİNDEN ÖNCE HZ. MUHAMMED', 'DKAB.6.4.2. Hz. Muhammed’in (sav) çocukluk yılları hakkında verilen bilgileri yorumlayabilme.', 'Hz. Muhammed’in çocukluk yılları hakkında etkileşimli zaman çizelgesi hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '6', '4. PEYGAMBERLİĞİNDEN ÖNCE HZ. MUHAMMED', 'DKAB.6.4.3. Hz. Muhammed’in (sav) gençlik yılları hakkında verilen bilgileri yapılandırabilme.', 'Peygamberin gennçlik yıllarını özetleyen tarih şeridi hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '6', '4. PEYGAMBERLİĞİNDEN ÖNCE HZ. MUHAMMED', 'DKAB.6.4.4. Fil suresini ve bu surenin anlamını okuyarak yorumlayabilme.', 'Surenin anlamını ve nasıl okunduğunu anlatan bir video, ses dosyası hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '6', '5. KÜLTÜRÜMÜZDEKİ DİNÎ MOTİFLER', 'DKAB.6.5.1. Geleneğimizde, edebiyatımızda ve musikimizde dinin izlerini özetleyebilme.', 'Öğrencilerin dini temaların edebiyat ve müzik üzerindeki etkilerine yönelik video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '7', 'MELEK VE AHİRET İNANCI', '7.1.2. Melekleri özellikleri ve görevlerine göre sınıflandırır.', '* Melekleri özellikleri ve görevlerine göre sınıflandırmaya yönelik etkileşimli içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '7', 'MELEK VE AHİRET İNANCI', '7.1.3. Dünya hayatı ile ahiret hayatı arasındaki ilişkiyi yorumlar.', '* Konuyu ayet ve hadislerle destekleyen video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '7', 'MELEK VE AHİRET İNANCI', '7.1.4. Ahiret hayatının aşamalarını açıklar.', '* Ahiret hayatına dair kavramlara ve açıklamalarına yönelik eşleştirme, doğru yanlış, boşluk doldurma veya benzeri etkileşimli içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '7', 'MELEK VE AHİRET İNANCI', '7.1.6. Hz. İsa’nın (a.s.) hayatını ana hatlarıyla tanır.', '* Hz. İsa’nın hayatına dair video, sunu veya etkileşimli içerik hazırlanır.', 'Video/Sunu/Etkile şimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '7', 'HAC VE KURBAN', '7.2.2. Haccın yapılışını özetler.', '* Haccın yapılışını anlatan video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '7', 'HAC VE KURBAN', '7.2.5. Hz. İsmail’in (a.s.) hayatını ana hatlarıyla tanır.', '* Hz. İsmail’in hayatına dair video, sunu veya etkileşimli içerik hazırlanır.', 'Video/Sunu/Etkile şimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '7', 'AHLAKİ DAVRANIŞLAR', '7.3.1. Güzel ahlaki tutum ve davranışları örneklerle açıklar.', '* Güzel ahlaki tutum ve davranışları anlatan video veya sunu hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '7', 'AHLAKİ DAVRANIŞLAR', '7.3.4. Hz. Salih’in (a.s.) hayatını ana hatlarıyla tanır.', '* Hz. Salih’in hayatına dair video, sunu veya etkileşimli içerik hazırlanır.', 'Video/Sunu/Etkile şimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '7', 'ALLAH’IN KULU VE ELÇİSİ: HZ. MUHAMMED', '7.4.2. Hz. Muhammed’in (s.a.v.) peygamberlik yönüyle ilgili özelliklerini ayırt eder.', '* Hz. Muhammed’in (s.a.v.) peygamberlik özelliklerinin anlatıldığı video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '7', 'İSLAM DÜŞÜNCESİNDE YORUMLAR', '7.5.4. Alevilik-Bektaşilikle ilgili temel kavram ve erkânları açıklar.', '* Aleviliğe dair kavramlara ve açıklamalarına yönelik boşluk doldurma, eşleştirme veya soru cevap şeklinde etkileşimli içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '4-8', 'ÜNİTE/TEMA/ ÖĞRENME ALANI', 'KAZANIM/ÖĞRENME ÇIKTISI/BÖLÜM', 'AÇIKLAMA', 'E-İÇERİK TÜRÜ', 'DİĞER');
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '8', 'KADER İNANCI', '8.1.1. Kader ve kaza inancını ayet ve hadislerle açıklar.', '* Evrendeki fiziksel, biyolojik ve toplumsal yasalara işaret eden örneklerden bazılarının sünnetullah kavramı çerçevesinde ele alındığı video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '8', 'KADER İNANCI', '8.1.3. Kaza ve kader ile ilgili kavramları analiz eder.', '* Kader, kaza, sünnetullah, küllî irade, cüzî irade kavramlarının açıklandığı infografik veya etkileşimli içerik hazırlanır.', 'İnfografik/Etkileşi mli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '8', 'KADER İNANCI', '8.1.5. Hz. Musa’nın (a.s.) hayatını ana hatlarıyla tanır.', '* Hz. Musa’nın Kur’an’daki kıssasından kesitler içeren video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '8', 'KADER İNANCI', '8.1.6. Ayet el-Kürsi''yi okur, anlamını söyler.', '* Ayet el-Kürsi’nin metni verilerek her bir ayetin veya tamamının üzerine tıklandığında Arapça okunuşunun dinlenebildiği ve metin üzerinde eş zamanlı olarak vurgulandığı etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '8', 'ZEKÂT VE SADAKA', '8.2.2. Zekât ve sadaka ibadetini ayet ve hadislerle açıklar.', '* Zekât hükümlerinin öğrenci seviyesine uygun şekilde ele alındığı infografik veya etkileşimli içerik hazırlanır.', 'İnfografik/Etkileşi mli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '8', 'ZEKÂT VE SADAKA', '8.2.4. Hz. Şuayb’in (a.s.) hayatını ana hatlarıyla tanır.', '* Hz. Şuayb (a.s.) kıssasında öne çıkan hususların vurgulandığı video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '8', 'ZEKÂT VE SADAKA', '8.2.5. Maûn suresini okur, anlamını söyler.', '* Maûn suresinin metni verilerek her bir ayetin veya tamamının üzerine tıklandığında Arapça okunuşunun dinlenebildiği ve metin üzerinde eş zamanlı olarak vurgulandığı etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '8', 'DİN VE HAYAT', '8.3.2. İslam dininin can, nesil, akıl, mal ve din emniyetiyle ilgili ortaya koyduğu ilke ve hedefleri analiz eder.', '* İslam dininin, korunmasını emrettiği hususlara yönelik etkileşimli içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '8', 'DİN VE HAYAT', '8.3.3. Hz. Yusuf’un (a.s.) örnek hayatından ilkeler çıkarır.', '* Kur’an’daki Hz. Yusuf kıssasından kesitler içeren video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '8', 'DİN VE HAYAT', '8.3.4. Asr suresini okur, anlamını söyler.', '* Asr suresinin metni verilerek her bir ayetin veya tamamının üzerine tıklandığında Arapça okunuşunun dinlenebildiği ve metin üzerinde eş zamanlı olarak vurgulandığı etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '8', 'HZ. MUHAMMED’İN ÖRNEKLİĞİ', '8.4.4. Hz. Muhammed’in (s.a.v.) cesaret ve kararlılığını örnek olaylarla açıklar.', '* Hz. Peygamber’in cesaret ve kararlılığına yönelik örnek olayların ele alındığı video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '8', 'HZ. MUHAMMED’İN ÖRNEKLİĞİ', '8.4.8. Hz. Muhammed’in (s.a.v.) hikmetli söz ve davranışlarıyla insanları iyiye ve güzele yönlendirdiğini fark eder.', '* Toplumdaki, Hz. Peygamber’in sünnetine dayanan örf ve âdetlerden kesitler sunan video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '8', 'HZ. MUHAMMED’İN ÖRNEKLİĞİ', '8.4.9. Kureyş suresini okur, anlamını söyler.', '* Kureyş suresinin metni verilerek her bir ayetin veya tamamının üzerine tıklandığında Arapça okunuşunun dinlenebildiği ve metin üzerinde eş zamanlı olarak vurgulandığı etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '8', 'KUR’AN-I KERİM VE ÖZELLİKLERİ', '8.5.2. Ayetlerden hareketle Kur’an’ın ana konularını sınıflandırır.', '* Kur’an-ı Kerim’in ana konularının başlıklar hâlinde sıralandığı ve öğrenci seviyesine uygun şekilde açıklandığı infografik veya sınıflamaya yönelik etkileşimli içerik hazırlanır.', 'İnfografik/Etkileşi mli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve Ahlak Bilgisi', '8', 'KUR’AN-I KERİM VE ÖZELLİKLERİ', '8.5.4. Hz. Nuh’un (a.s.) tevhide davetini özetler.', '* Hz. Nuh’un kıssasındaki özellikle tevhide vurgu yapan hususların öne çıkarıldığı video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '9', '1. ALLAH-İNSAN İLİŞKİSİ', 'DKAB.9.1.2. İnsanın doğruyu arayan bir varlık olarak bilgiye ulaşma yolları hakkında eleştirel düşünebilme', 'İnsanın vahiy ve peygambere olan ihtiyacını vurgulayan, konuyla ilgili örnek ayetler içeren video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '9', '1. ALLAH-İNSAN İLİŞKİSİ', 'DKAB.9.1.3. Allah (cc) ile iletişimin ibadet ve dua ile sağlandığını yansıtabilme', 'Dua ve ibadetlerin insan hayatına nasıl pozitif etkilerde bulunduğunu, zorluklarla mücadelede nasıl güç ve moral verdiğini anlatan bir video, etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '9', '1. ALLAH-İNSAN İLİŞKİSİ', 'DKAB.9.1.4. Rum suresi 17-27. ayetlerin mesajlarını özetleyebilme', 'Rum suresinin 17-27. ayetleri ve bu ayetlerin meallerini içeren video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '9', '2. İSLAM’DA İNANÇ ESASLARI', 'Köprü Kurma', 'Geçmişteki her toplumun yüce bir varlık inancına sahip olmasıyla ilgili video hazırlanır.', 'Video', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '9', '2. İSLAM’DA İNANÇ ESASLARI', 'DKAB.9.2.1. İman ve imanın mahiyetiyle ilgili konuları sorgulayabilme', 'İmanın mahiyetini ve insanın inanma duygusunu tarihsel ve kültürel bağlamda ele alan video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '9', '2. İSLAM’DA İNANÇ ESASLARI', 'DKAB.9.2.2. İslam’ın iman esaslarını ayet ve hadisler bağlamında özetleyebilme', 'Her bir imanın esasını ayrı ayrı ele alan ve her iman esasıyla ilgili ayet ve hadisler içeren etkileşimli bilgi kartları hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '9', '2. İSLAM’DA İNANÇ ESASLARI', 'DKAB.9.2.3. İmanın bireye ve topluma kazandırdıklarını yansıtabilme', 'Farklı karakterlerin hayat hikâyelerine yer verilerek imanın bireysel ve toplumsal faydalarını anlatan video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '9', '2. İSLAM’DA İNANÇ ESASLARI', 'DKAB.9.2.4. Bakara suresinin 177. ayetindeki mesajları özetleyebilme', 'Bakara suresi 177. ayeti ve bu ayetin mealini içeren video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '9', '3. İSLAM’DA İBADETLER', 'DKAB.9.3.1. İslam’da ibadet ve ibadetin temel ilkeleri hakkında bilgi toplayabilme', 'İslam’da ibadet ve ibadetin temel ilkeleri hakkında bilgilerin yer aldığı temel kaynaklardan etkileşimli bilgi kartları hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '9', '3. İSLAM’DA İBADETLER', 'DKAB.9.3.2. İslam’da temel ibadetleri sınıflandırabilme', 'Abdest almayı ve namaz kılmayı gösteren kısa bir video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '9', '3. İSLAM’DA İBADETLER', 'DKAB.9.3.3. İnsan ve ibadet ilişkisini yorumlayabilme', 'İbadetlerin insanı Allah’a (cc) yakınlaştırması, güzel ahlaka yönlendirmesi, insanda yardımlaşma duygusunu, sabır ve disiplini geliştirmesini vurgulayan video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '9', '3. İSLAM’DA İBADETLER', 'DKAB.9.3.4. Bakara suresi 21, Zariyat suresi 56 ve Hicr suresi 99. ayetlerin mesajlarını özetleyebilme', 'Bakara suresi 21. ayeti ve bu ayetin mealini içeren video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '9', '3. İSLAM’DA İBADETLER', 'DKAB.9.3.4. Bakara suresi 21, Zariyat suresi 56 ve Hicr suresi 99. ayetlerin mesajlarını özetleyebilme', 'Zariyat suresi 56. ayeti ve bu ayetin mealini içeren video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '9', '3. İSLAM’DA İBADETLER', 'DKAB.9.3.4. Bakara suresi 21, Zariyat suresi 56 ve Hicr suresi 99. ayetlerin mesajlarını özetleyebilme', 'Hicr suresi 99. ayeti ve bu ayetin mealini içeren video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '9', '4. İSLAM’DA AHLAK İLKELERİ', 'DKAB.9.4.2. Ahlakın temel unsurlarını sentezleyebilme', 'Ahlakın temel unsurları olan adalet, şecaat, iffet ve hikmet ile ilgili etkileşimli bilgi kartları hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '9', '4. İSLAM’DA AHLAK İLKELERİ', 'DKAB.9.4.3. Ahlaki tutum ve davranışlarla ilgili eleştirel düşünebilme', 'Sınavda kopya çekme veya çekmeme ya da zor durumda kalan bir arkadaşına yardım etme veya etmeme gibi dürüstlük, yardımseverlik, sabır konularında ikilemlerde kalınan ve alınan kararların olumlu ve olumsuz sonuçlarını gösteren video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '9', '4. İSLAM’DA AHLAK İLKELERİ', 'DKAB.9.4.4. İsra suresi 23-29. ayetlerdeki mesajları özetleyebilme', 'İsra suresi 23-29.ayetleri ve bu ayetlerin mealini içeren video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '9', '5. KUR’AN’A GÖRE HZ. MUHAMMED', 'DKAB.9.5.1. Hz. Muhammed’in (sav) beşerî ve peygamberlik yönünü özetleyebilme', 'Hz. Muhammed’e (sav) peygamberlik görevi verilmeden önceki dönemi coğrafi, sosyo-kültürel, ekonomik ve dinî açılardan anlatan belgesel veya film hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '9', '5. KUR’AN’A GÖRE HZ. MUHAMMED', 'DKAB.9.5.2. Hz. Muhammed’in (sav) örnekliğini esas alarak kendi bakış açısını yapılandırabilme', 'Hz. Muhammed’in (sav) gençlerle olan iletişiminde onlarla olan dostluğuna ve dayanışmasına dair örnekler içeren video veya etkileşimli video hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '9', '5. KUR’AN’A GÖRE HZ. MUHAMMED', 'DKAB.9.5.3. Kültürümüzde Hz. Muhammed (sav) ve Ehlibeyt sevgisini yorumlayabilme', 'Kültürümüzde yer alan Hz. Peygamber ve Ehlibeyt sevgisi ile ilgili şiir, naat, mevlit gibi eserlerden örnek video/ses hazırlanır.', 'Video/Ses', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '9', '5. KUR’AN’A GÖRE HZ. MUHAMMED', 'DKAB.9.5.4. Ahzab suresinin 45-46 ve Nahl suresinin 43-
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '9', '5. KUR’AN’A GÖRE HZ. MUHAMMED', 'DKAB.9.5.4. Ahzab suresinin 45-46 ve Nahl suresinin 43-
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '10', '1. İSLAM''DA VARLIK VE BİLGİ', 'DKAB.10.1.1. İslam’da bilginin yerini ve kaynaklarını
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '10', '1. İSLAM''DA VARLIK VE BİLGİ', 'DKAB.10.1.2. Allah-âlem ilişkisini tefekkür ederek sorgulayabilme.', 'Allah-âlem-insan ilişkisine yönelik içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '10', '1. İSLAM''DA VARLIK VE BİLGİ', 'DKAB.10.1.3. İsra suresi 36 ve Mülk suresi 23. ayetlerin mesajlarını özetleyebilme.', 'İsra suresi 36 ve Mülk suresi 23. ayetlerinde verilen mesajlarla ilgili içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '10', '2. ALLAH''I TANIMAK', 'DKAB.10.2.1. Allah’ın (cc) varlığının delilleri üzerine akıl yürütebilme.', 'Allah’ın varlığı ve birliği konusunda akli ve nakli delilleri karşılaştırmalı olarak sınıflandırmaya yönelik içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '10', '2. ALLAH''I TANIMAK', 'DKAB.10.2.2. Allah’ı (cc) gereği gibi tanıyabilmek için O’nun isim ve sıfatlarını hayata yansıması bakımından sentezleyebilme.', 'Allah’ın isim ve sıfatlarının kâinata tecellisini örneklerle ele alan içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '10', '2. ALLAH''I TANIMAK', 'DKAB.10.2.3. Haşr suresi 22-24. ayetlerin mesajlarını özetleyebilme.', 'Haşr suresi 22-24. Ayetler verilerek her bir ayetin veya tamamının üzerine tıklandığında Arapça okunuşunun dinlenebildiği ve metin üzerinde eş zamanlı olarak vurgulandığı etkileşimli içerik hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '10', '3. İSLAM''IN EVRENSEL MESAJLARI', 'DKAB.10.3.2. İslam’ın adalet ve eşitlik ilkeleri arasındaki ilişkiyi sentezleyebilme.', 'Bütün insanların Allah (cc) katında eşit oluşu; kadın-erkek, işveren-işçi, amir-memur, zengin-fakir gibi ayrımların İslam’a göre insanlara bir üstünlük sağlamadığı, insanı değerli kılan özelliğin takva olduğu vurgulayan içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '10', '3. İSLAM''IN EVRENSEL MESAJLARI', 'DKAB.10.3.3. İslam ve barış ilişkisini yorumlayabilme.', 'İslam ve barış ilişkisi, İslam savaş hukuku ve savaş ahlakı hakkında yapılan örnek çalışmalara yönelik içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '10', '3. İSLAM''IN EVRENSEL MESAJLARI', 'DKAB.10.3.4. Nahl suresi 90, Nisa suresi 58 ve Bakara suresi 208. ayetlerin mesajlarını özetleyebilme.', 'İlgili ayetlerin meal ve tefsirleri matbu ve dijital yayımlardan bulunarak incelenir, bu ayetlerde verilen mesajları içeren içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '10', '4. DİN, ÇEVRE VE TEKNOLOJİ', 'DKAB.10.4.1. İnsan, çevre ve ahlak arasındaki ilişkiyi yorumlayabilme.', 'İnsan, çevre ve ahlak arasındaki ilişkiyi, insanın çevreye karşı ahlaki sorumluluklarını, israfın zararlarını, İslam’ın çevreye dair temel ahlaki ilkelerini, çevre sorunlarına yaklaşımını ve çözüm önerilerini anlatan içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '10', '4. DİN, ÇEVRE VE TEKNOLOJİ', 'DKAB.10.4.2. Teknoloji ve ahlak ilişkisini çözümleyebilme.', 'Teknoloji ve ahlak arasındaki ilişkinin güncel teknolojik gelişmelerle bağlantı kurularak incelendiği, sosyal medya, yapay zekâ, dijital oyunlar, kişisel verilerin korunması, mahremiyet ilkelerine uygun davranma konularını içeren içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '10', '4. DİN, ÇEVRE VE TEKNOLOJİ', 'DKAB.10.4.3. Çevre ve teknoloji ilişkisini özetleyebilme.', 'Teknolojik gelişmelerin sebep olduğu çevre problemleri ile ilgili (iklim değişikliği, ormanların yok edilmesi, nesli tükenme tehlikesi altında olan hayvanlar, çarpık kentleşme, tüketim bağımlılığı vb.) içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '10', '4. DİN, ÇEVRE VE TEKNOLOJİ', 'DKAB.10.4.4. Rum suresi 41. ayetin mesajlarını özetleyebilme.', 'Rum suresinin 41. ayetinin mesajlarına yönelik içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '10', '5. İSLAM DÜŞÜNCESİNDE İTİKADİ- SİYASİ VE FIKHİ YORUMLAR', 'DKAB.10.5.1. Dinî yorum farklılıklarının ortaya çıkış sebeplerini sentezleyebilme.', 'İslam düşüncesinde ortaya çıkan yorum farklılıklarının ortaya çıkış sebeplerine yönelik içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '10', '5. İSLAM DÜŞÜNCESİNDE İTİKADİ- SİYASİ VE FIKHİ YORUMLAR', 'DKAB.10.5.2. İslam düşüncesindeki itikadi-siyasi ve fıkhi yorumları karşılaştırabilme.', 'İslam düşüncesinde itikadi - siyasi ve fıkhi yorumların genel özelliklerine yönelik içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '10', '5. İSLAM DÜŞÜNCESİNDE İTİKADİ- SİYASİ VE FIKHİ YORUMLAR', 'DKAB.10.5.3. Hucurat suresi 13. ayetin mesajını özetleyebilme.', 'Hucurât Suresi 13. ayetinin mesajlarına yönelik içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '11', 'DÜNYA VE AHİRET', '11.1.2. Dünya hayatı ile ahiret hayatı arasında ilişki kurar.', '* Konuyla ilgili temel kavramlar ve açıklamalarına yönelik eşleştirme, boşluk doldurma, soru cevap veya benzeri etkileşimli içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '11', 'DÜNYA VE AHİRET', '11.1.3. Ahiret hayatının aşamalarını ayet ve hadislerle temellendirir.', '* Konuyla ilgili ayet ve hadislerden yararlanarak görsel ve işitsel ögeler içeren video, sunu veya etkileşimli içerik hazırlanır.', 'Video/Sunu/Etkile şimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '11', 'DÜNYA VE AHİRET', '11.1.4. Cenaze uğurlama ile ilgili dinî uygulamaları örneklerle açıklar.', '* Konuyla ilgili temel kavramlar ve açıklamalarına yönelik eşleştirme, boşluk doldurma, soru cevap veya benzeri etkileşimli içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '11', 'KUR’AN’A GÖRE HZ. MUHAMMED', '11.2.2. Hz. Muhammed’in peygamberlikle ilgili görevlerini açıklar.', '*Hz. Muhammed’in peygamberlikle ilgili görevlerine yönelik infografik veya etkileşimli içerik hazırlanır.', 'İnfografik/Etkileşi mli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '11', 'KUR’AN’DA BAZI KAVRAMLAR', '11.3.2. Kur’an’ı Kerim’de geçen kavramları tanımanın İslam’ı doğru anlamadaki önemini fark eder.', '* Konuyla ilgili temel kavramlar ve açıklamalarına yönelik etkileşimli kavram haritası  hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '11', 'İNANÇLA İLGİLİ MESELELER', '11.4.3. En’âm suresi 59 ve Lokmân suresi 27. ayetlerde verilen mesajları değerlendirir.', '* En’âm Suresi 59. ayet ve Lokmân Suresi 27. ayetten hareketle İslam’ın ilim anlayışı, ilmin sınırları, geleceği belirlemede ilmin rolünü konu alan video, sunu veya etkileşimli içerik hazırlanır.', 'Video/Sunu/Etkile şimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '11', 'YAHUDİLİK VE HIRİSTİYANLIK', '11.5.1. Yahudiliğin doğuşunu ve gelişim sürecini özetler', '* Yahudiliğin doğuş ve gelişim sürecini gösteren video, infografik veya etkileşimli içerik hazırlanır.', 'Video/İnfografik/E tkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '11', 'YAHUDİLİK VE HIRİSTİYANLIK', '11.5.2. Hıristiyanlığın doğuşunu ve gelişim sürecini özetler.', '* Hristiyanlığın doğuşunu ve gelişim sürecini gösteren video, infografik veya etkileşimli içerik hazırlanır.', 'Video/İnfografik/E tkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '12', 'İSLAM VE BİLİM', '12.1.1. Din-bilim ilişkisini tartışır.', '* Din bilim ilişkisini tarihsel süreç içerisinde anlatan video, sunu veya etkileşimli içerik hazırlanır.', 'Video/Sunu/Etkile şimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '12', 'İSLAM VE BİLİM', '12.1.2. İslam medeniyetinde bilim ve düşüncenin gelişim sürecini değerlendirir.', '* İslam medeniyetinde bilim ve düşüncenin gelişim süreciyle ilgili video, infografik veya etkileşimli içerik hazırlanır.', 'Video/İnfografik/E tkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '12', 'İSLAM VE BİLİM', '12.1.3. İslam medeniyetinde öne çıkan eğitim ve bilim kurumlarını tanır.', '* İslam medeniyetinde öne çıkan kurumlarla ilgili video, infografik veya etkileşimli içerik hazırlanır.', 'Video/İnfografik/E tkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '12', 'İSLAM VE BİLİM', '12.1.4. Müslümanların bilim alanında yaptığı özgün çalışmaları sınıflandırır.', '* Müslümanların bilim alanında yaptığı özgün çalışmaları tanıtan video, infografik veya etkileşimli içerik hazırlanır.', 'Video/İnfografik/E tkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '12', 'İSLAM VE BİLİM', '12.1.5. Fâtır suresi 27-28. ayette verilen mesajları değerlendirir.', '* Fâtır suresi 27-28. ayette verilen mesajları açıklayan video, sunu veya etkileşimli içerik hazırlanır.', 'Video/Sunu/Etkile şimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '12', 'ANADOLU’DA İSLAM', '12.2.1. Türklerin Müslüman olma sürecini açıklar.', '* Türklerin Müslüman olma sürecini açıklayan video, sunu veya etkileşimli içerik hazırlanır.', 'Video/Sunu/Etkile şimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '12', 'ANADOLU’DA İSLAM', '12.2.2. Dinî anlayış ve kültürümüzün oluşmasında etkili olan bazı şahsiyetleri tanır.', '* Dinî anlayış ve kültürümüzün oluşmasında etkili olan bazı şahsiyetleri tanıtan video, infografik veya etkileşimli içerik hazırlanır.', 'Video/İnfografik/E tkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Din Kültürü ve AhlakBilgisi', '12', 'ANADOLU’DA İSLAM', '12.2.3. Nisâ suresi 69. ayette verilen mesajları değerlendirir.', '* Nisâ suresi 69. ayette verilen mesajları açıklayan video, sunu veya etkileşimli içerik hazırlanır.', 'Video/Sunu/Etkile şimli İçerik', NULL);
