INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '1.ÜNİTE HADIS İLMINE GIRIŞ', NULL, 'Hz. Muhammed’in (sav) bir peygamber olarak vahyi insanlara bildirmedeki rolü; tebliğ, tebyin, teşri, temsil ve tekit kavramlarıyla ilgili içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '1.ÜNİTE HADIS İLMINE GIRIŞ', NULL, 'Öğrencilerin hadis-sünnet, rivayet, ravi, senet-isnat ve metin şeklinde
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '1.ÜNİTE HADIS İLMINE GIRIŞ', NULL, 'Sünnetin dindeki yeri ve bağlayıcılığı konusuyla ilgili ayetlerin yer aldığı içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '1.ÜNİTE HADIS İLMINE GIRIŞ', NULL, 'Sünnetin evrenselliğini  örnekler üzerinden açıklayan içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '1.ÜNİTE HADIS İLMINE GIRIŞ', NULL, 'Kur’an ve Sünnete bağlılığın önemini vurgulayan örnek bir hadisin metninin, sunulduğu bir video ya da Kur''an ve sünnete bağlılık konusunu destekleyen bir video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '2. ÜNİTE: HADİS TARİHİ', NULL, 'Hadislerin Sözlü ve Yazılı Aktarım sürecinin yer aldığı öğrencilerin bu süreci somutlaştırabilecekleri ve öğrenmeyi kolaylaştırabilecek ieçrik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '2. ÜNİTE: HADİS TARİHİ', NULL, 'Tedvin ve tasnif döneminde hadis ilminde önemli isimlerin yer aldığı kavram ağacı hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '2. ÜNİTE: HADİS TARİHİ', NULL, 'Tedvin ve tasnif sonrası hadis çalışmaları ile ilgili içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '2. ÜNİTE: HADİS TARİHİ', NULL, 'İmanla ilgili hadisleri güvenilir hadis kaynaklarının ilgili bölümlerini içeren bilgi kartları hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '2. ÜNİTE: HADİS TARİHİ', NULL, 'İmanla ilgili örnek bir hadisin metninin sunulduğu bir video ya da iman konusunu destekleyen bir video hazırlanır.
