INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '3. ÜNİTE: HADİS ÇEŞİTLERİ', NULL, 'Sıhhat derecesine göre hadis çeşitleriyle ilgili etkileşimli soru veya bir
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '3. ÜNİTE: HADİS ÇEŞİTLERİ', NULL, 'Ahlak ve muâmelatla ilgili bir hadislerin yer aldığı içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '3. ÜNİTE: HADİS ÇEŞİTLERİ', NULL, 'Ahlakla ilgili örnek bir hadisin metninin sunulduğu bir video ya da iman konusunu destekleyen bir video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '4.ÜNİTE: HADİS VE SÜNNETİN ANLAŞILMASI', NULL, 'Hadis ve sünnetin anlaşılmasını konu alan ilimlerle ilgili içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '4.ÜNİTE: HADİS VE SÜNNETİN ANLAŞILMASI', NULL, 'Hadis ve sünnetin anlaşılmasını konu alan ilimlerle ilgili içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
