INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '4.ÜNİTE: HADİS VE SÜNNETİN ANLAŞILMASI', NULL, 'İlmin önemini vurgulayan örnek bir hadisin metninin, sunulduğu bir video ya da İlmin önemini destekleyen bir video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '1. ÜNİTE: İSLAM ÖNCESİ DÖNEM: HİCAZ VE HZ. MUHAMMED', NULL, 'Hicaz’ın önemli şehirlerinin tarihî, coğrafi ve ekonomik özelliklerini gösteren içerik hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '1. ÜNİTE: İSLAM ÖNCESİ DÖNEM: HİCAZ VE HZ. MUHAMMED', NULL, 'Hz. Muhammed''in doğduğu dönemin Mekke ve çevresindeki kabile yaşantısı, inanç sistemleri, ticaret ve günlük hayat pratikleri gibi sosyal ve kültürel unsurları görsel örneklerle açıklayan bir video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '1. ÜNİTE: İSLAM ÖNCESİ DÖNEM: HİCAZ VE HZ. MUHAMMED', NULL, 'Hz. Muhammed''in risalet öncesi çocukluk, gençlik ve olgunluk dönemlerine ait önemli olayları ve şahsiyetini anlatan, sorgulayıcı soruları barındıran kısa bir belgesel video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '2. ÜNİTE
