INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '3. ÜNİTE: İSLAM’IN YAYILIŞI: MEDİNE YILLARI', NULL, 'Medine Sözleşmesi, Suffe, kardeşlik ilanı gibi olaylar üzerinden Medine’de İslam toplumunun kuruluş ve gelişim sürecini ana hatlarıyla sentezleyen bir belgesel video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '3. ÜNİTE: İSLAM’IN YAYILIŞI: MEDİNE YILLARI', NULL, 'Hz. Peygamber’in Medine döneminde Yahudiler, Hristiyanlar ve diğer inanç gruplarıyla kurduğu ilişkileri, yapılan anlaşmaları ve müsamaha prensiplerini örnek olaylarla açıklayan bir video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '3. ÜNİTE: İSLAM’IN YAYILIŞI: MEDİNE YILLARI', NULL, 'Hudeybiye Antlaşması, fetihler, heyetlerin gönderilmesi gibi İslam’ın yayılmasında etkili olan temel faaliyetleri harita animasyonları ve görsellerle müşahede ettiren bir video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '3. ÜNİTE: İSLAM’IN YAYILIŞI: MEDİNE YILLARI', NULL, 'Veda Hutbesi''nin genel içeriğini, temel evrensel mesajlarını ve önemini vurgulayan, orijinal metinlerden alıntılar içeren bir video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '3. ÜNİTE: İSLAM’IN YAYILIŞI: MEDİNE YILLARI', NULL, 'Hz. Peygamber''in son günleri, hastalığı ve vefatı sırasında yaşananları kronolojik olarak anlatan, sahabelerin bu süreçteki tutumlarını ve duygu durumlarını aktaran kısa bir drama veya belgesel video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '4. ÜNİTE
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '4. ÜNİTE: PEYGAMBERİMİZİN AHLAKI: İNSANLIĞA MİRAS', NULL, 'Hz. Peygamber sevgisinin musikideki tezahürlerinden ilahi, mevlit, miraciye ve tevşih okunuşlarına dair örnekler içeren ses dosyaları veya videolar hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '1. ÜNİTE: FIKIH İLMİ VE TARİHİ', NULL, 'Fıkıh ve fıkıh usulü alanındaki temel kaynakların tanıtıldığı video
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '1. ÜNİTE: FIKIH İLMİ VE TARİHİ', NULL, 'Fıkıh ilminin temel ilkelerine ( kolaylık, helallerde genişlik, kamu yararını gözetmek ve adaleti gözetmek) örnekler verilerek temel
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '2. ÜNİTE: FIKIH İLMİ VE TARİHİ', NULL, 'Mezheplerin belirgin özelliklerini ve içtihat yöntemlerini gösteren etkileşimli içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '1. ÜNİTE: FIKIH İLMİ VE TARİHİ', NULL, 'Mezhep imamlarını tanıtan içerik hazırlanır.', 'Video', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '1. ÜNİTE: FIKIH İLMİ VE TARİHİ', NULL, 'Fıkıh mezheplerin oluşumundan sonraki dönemlerde yaşanan
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '2. ÜNİTE: FIKHİ HÜKÜMLER VE DELİLLERİ', NULL, 'Fıkhi hükümlerle ilgili temel dinî kavramların (farz, vacip, sünnet, müstehap, mubah, mekruh, haram) tanımlarının yapıldığı ve bu kavramlar arasındaki farkların günlük hayattan örneklerle açıklandığı
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '3. ÜNİTE: FIKHİ HÜKÜMLER VE DELİLLERİ', NULL, 'Öğrencilere çeşitli durumlar ve davranışlar sunularak bunları fıkhi hüküm kavramlarına (farz, vacip, sünnet vb.) göre sınıflandırmaları istenen ve her seçim sonrası açıklayıcı geri bildirimler aldıkları bir eşleştirme etkinliği hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '2. ÜNİTE: FIKHİ HÜKÜMLER VE
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '3. ÜNİTE: İBADETLER', NULL, 'Namazın farzları, vacipleri ve sünnetleri ile namazı bozan durumların ayrıntılı bir şekilde açıklandığı ve farklı namazların (örneğin Cuma, bayram, cenaze namazları) kılınış şekillerinin gösterildiği içerik hazırlanır.', 'Video', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '3. ÜNİTE: İBADETLER', NULL, 'Ramazan ayı ve oruç ile ilgili kavramlardan fidye, fitre/fıtır sadakası itikaf, kaza, kefaret, mukabele, rüyetihilal, teravih kavramlarına yer verilen içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '3. ÜNİTE: İBADETLER', NULL, 'Zekât, sadaka ve infak kavramlarını açıklayan içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '3. ÜNİTE: İBADETLER', NULL, 'Zekâtın kimlere farz olduğu, hangi mallardan ne kadar verileceği, zekât verilecek kimseler ve zekâtın toplumsal faydaları gibi konuların şemalar ve örnek hesaplamalarla anlatıldığı bir bilgilendirme videosu hazırlanır.', 'Video', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '3. ÜNİTE: İBADETLER', NULL, 'Havaiciasliyye, havelanulhavl, nisap, temlik ve öşür kavramlarına yer
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '3. ÜNİTE: İBADETLER', NULL, 'Hac ibadetinin temel rükünleri, vacipleri, sünnetleri ve yasaklarının açıklandığı, ayrıca hacla ilgili fıkhi hükümlere ulaşmada başvurulabilecek temel eserlerin (ilmihaller, fıkıh kitapları) ve
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '3. ÜNİTE: İBADETLER', NULL, 'Hacla ilgili kavramlar ve mekânlardan ihram, istilam, say, şavt, tavaf, vakfe, mikat, Mina ve Müzdelife’ye yer verilen içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '3. ÜNİTE: İBADETLER', NULL, 'Vatanı koruma ve savunmada cihadın rolunu anlatan içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '4. ÜNİTE: SOSYAL VE EKONOMIK HAYAT', NULL, 'Bunun yerine İslam''da aile hayatına verilen öenmi, esşlerin hak ve sorumluluklar gibi konuları içeren içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '4. ÜNİTE: SOSYAL VE EKONOMIK HAYAT', NULL, 'İslam ekonomisinin temel prensipleri olan faiz yasağı, helal kazanç, israftan kaçınma, emek ve mülkiyet hakkı gibi konuların ve bu konulardaki farklı fıkhi görüşlerin yer aldığı video hazırlanır.', 'Video', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '4. ÜNİTE: SOSYAL VE EKONOMIK HAYAT', NULL, 'Faiz yasağı, sigortanın hükmü, karaborsacılık, yapay olarak fiyatlarla oynama, borsa, dijital ticaret, telif, intihal, şans oyunları ile ilgili içerik
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '4. ÜNİTE: SOSYAL VE EKONOMIK HAYAT', NULL, 'Yiyecekler, içecekler, giyim kuşam, kazanç yolları ve sosyal ilişkiler gibi hayatın farklı alanlarındaki helal ve haram sınırlarının Kur''an ve Sünnet''ten delillerle açıklandığı ve bu hükümlerin hikmetlerine değinildiği içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '4. ÜNİTE: SOSYAL VE EKONOMIK
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '4. ÜNİTE: SOSYAL VE EKONOMIK
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('İHL MESLEK DERSLER', '10', '4. ÜNİTE: SOSYAL VE EKONOMIK HAYAT', NULL, 'Öğrencilerin İslam ceza hukukuyla ilgili temel kavramları, suç türlerini ve bunlara karşılık gelen cezaları eşleştirebildikleri, kavram haritası oluşturma veya boşluk doldurma etkinliği hazırlannır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '5', '1. KUR’AN-I KERİM’İ TANIYORUM', 'KK.5.1.1. Kur’an-ı Kerim’i öğrenmenin önemini ve okumanın adabını çözümleyebilme', 'Kur’an-ı Kerim’i öğrenmenin önemi ve okumanın adabına ilişkin video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '5', '1. KUR’AN-I KERİM’İ TANIYORUM', 'KK.5.1.2. Harfleri seslendirebilme', 'Harflerin doğru telaffuzu ile ilgili video veya etkileşimli içerik hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '5', '1. KUR’AN-I KERİM’İ TANIYORUM', 'KK.5.1.3. Harfleri yazabilme', 'Kur’an harflerinin başta, ortada ve sonda yazılışlarını gösteren video veya etkileşimli video hazırlanır.
