INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '5', '1. KUR’AN-I KERİM’İ TANIYORUM', 'KK.5.1.5. Sübhaneke duası ve Rabbena dualarının anlamlarını yorumlayabilme', 'Sübhaneke duasının anlamını, Arapça metinleri, Türkçe mealleri ve görsel öğelerle açıklayan video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '5', '1. KUR’AN-I KERİM’İ TANIYORUM', 'KK.5.1.5. Sübhaneke duası ve Rabbena dualarının anlamlarını yorumlayabilme', 'Rabbena duasının anlamını, Arapça metinleri, Türkçe mealleri ve görsel öğelerle açıklayan video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '5', '1. KUR’AN-I KERİM’İ TANIYORUM', 'KK.5.1.6. Sübhaneke duası ve Rabbena dualarını ezberleyebilme', 'Sübhaneke duasının okunuşu ve ezberlenmesiyle ilgili okunuşunun dinlenebildiği ve metin üzerinde eş zamanlı olarak vurgulandığı video veya etkileşimli video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '5', '1. KUR’AN-I KERİM’İ TANIYORUM', 'KK.5.1.6. Sübhaneke duası ve Rabbena dualarını ezberleyebilme', 'Rabbena duasının okunuşu ve ezberlenmesiyle ilgili okunuşunun dinlenebildiği ve metin üzerinde eş zamanlı olarak vurgulandığı video veya etkileşimli video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '5', '2. KUR’AN-I KERİM OKUMAYA GİRİŞ', 'KK.5.2.1. Cezimi ve Şeddeyi seslendirebilme', 'Cezim (Sükun) ve şedde işaretinin tanıtımı, şedde ile cezimin bağlantısı, şedde ve cezimli örneklerin yer
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '5', '2. KUR’AN-I KERİM OKUMAYA GİRİŞ', 'KK.5.2.2. Tenvini seslendirebilme', 'Tenvin işaretli harflerin (Üstün), (esre) ve (ötre) için doğru telaffuzu ile ilgili video, ses veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik/Ses', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '5', '2. KUR’AN-I KERİM OKUMAYA GİRİŞ', 'KK.5.2.3. Tahiyyat duasının anlamını yorumlayabilme', 'Tahiyyat duasının nerelerde okunduğu, anlamı ve bu duadaki mesajların neler olduğuyla ilgili video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '5', '2. KUR’AN-I KERİM OKUMAYA GİRİŞ', 'KK.5.2.4. Tahiyyat duasını ezberleyebilme', 'Tahiyyat duasının okunuşu ve ezberlenmesiyle ilgili okunuşunun dinlenebildiği ve metin üzerinde eş zamanlı olarak vurgulandığı video veya etkileşimli video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '5', '3. KUR’AN-I KERİM’İ ÖĞRENİYORUM', 'KK.5.3.1. Med harflerini sınıflandırabilme', 'Öğrencilerin med harflerini örneklerle tanımaları, ayrıştırmaları ve tasnif etmeleri için doğru telaffuzlarını da içeren video veya ses dosyası hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '5', '3. KUR’AN-I KERİM’İ ÖĞRENİYORUM', 'KK.5.3.2. Âsar-çeker, okunmayan elif, vav ve ya şeklinde yazılan elif, elif lâm takısı, lafzatullah, med ve kasır, zamir ve hurufumukattaayı seslendirebilme', 'Âsar, çeker, okunmayan elif, vav ve ya şeklinde yazılan elif, elif lâm takısı, lafzatullah, med ve kasır, zamir ve hurufumukattaa gibi tecvit kavramlarını açıklayan, örneklerle zenginleştirilmiş video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '5', '3. KUR’AN-I KERİM’İ ÖĞRENİYORUM', 'KK.5.3.3. Fatiha suresi ve Bakara suresi 1-24. ayetlerini yüzünden okuyabilme', 'Fatiha suresinin okunuşunun dinlenebildiği, metin üzerinde eş zamanlı olarak vurgulandığı video veya etkileşimli içerik hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '5', '3. KUR’AN-I KERİM’İ ÖĞRENİYORUM', 'KK.5.3.3. Fatiha suresi ve Bakara suresi 1-24. ayetlerini yüzünden okuyabilme', 'Bakara suresinin okunuşunun dinlenebildiği, metin üzerinde eş zamanlı olarak vurgulandığı video veya etkileşimli içerik hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '5', '3. KUR’AN-I KERİM’İ ÖĞRENİYORUM', 'KK.5.3.4. Fatiha suresinin anlamını yorumlayabilme', 'Fatiha suresinin okunduğu yerleri ve anlamını, hamt ve sıratımüstakim kavramlarını vurgulayan video veya etkileşimli video hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '5', '3. KUR’AN-I KERİM’İ ÖĞRENİYORUM', 'KK.5.3.5. Fatiha suresini ezberleyebilme', 'Fatiha suresini ezberlemeye yönelik video veya ses kaydı hazırlanır.', 'Video/Ses', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '5', '4. KUR’AN-I KERİM’İ OKUYORUM', 'KK.5.4.1. Med çeşitlerini sınıflandırabilme', 'Med çeşitlerini (medd-i tabiî, medd-i muttasıl, medd-i munfasıl) ve bu medlerin okunuşlarını örneklerle açıklayan ve tıklama yoluyla istenilen bölümlerin tekrar dinlenebildiği/izlenebildiği ve sınıflamaların yapılabildiği etkileşimli içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '5', '4. KUR’AN-I KERİM’İ OKUYORUM', 'KK.5.4.2. Bakara suresi 25-83. ayetleri yüzünden okuyabilme', 'Bakara suresinin 25-83. Ayetlerinin okunuşunun dinlenebildiği, metin üzerinde eş zamanlı olarak vurgulandığı video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '5', '4. KUR’AN-I KERİM’İ OKUYORUM', 'KK.5.4.3. Hz. İbrahim’in duasını yorumlayabilme', 'Hz. İbrahim''in duasının anlamını ve önemini anlatan bir video veya etkileşimli video hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '5', '4. KUR’AN-I KERİM’İ OKUYORUM', 'KK.5.4.4. İhlas ve Kevser surelerinin anlamlarını yorumlayabilme', 'İhlas suresinin anlamını ve içerdiği mesajları anlatan video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '5', '4. KUR’AN-I KERİM’İ OKUYORUM', 'KK.5.4.4. İhlas ve Kevser surelerinin anlamlarını yorumlayabilme', 'Kevser suresinin anlamını ve içerdiği mesajları anlatan video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '5', '4. KUR’AN-I KERİM’İ OKUYORUM', 'KK.5.4.5. İhlas ve Kevser surelerini ezberleyebilme', 'İhlas suresini ezberlemeyi kolaylaştıracak video, etkileşimli içerik veya ses kaydı hazırlanır.', 'Video/Etkileşimli İçerik/Ses', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '5', '4. KUR’AN-I KERİM’İ OKUYORUM', 'KK.5.4.5. İhlas ve Kevser surelerini ezberleyebilme', 'Kevser suresini ezberlemeyi kolaylaştıracak video, etkileşimli içerik veya ses kaydı hazırlanır.', 'Video/Etkileşimli İçerik/Ses', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '6', '1. KUR’AN-I KERİM’İ TANIYORUM', 'KK.6.1.1. Kur’an-ı Kerim’in iç düzenini sağlayan ayet, sure, hizip ve cüzü Kur’an’dan bulabilme', 'Kur’an-ı Kerim’in iç düzenini sağlayan ayet, sure, hizip ve cüzü Kur’an’dan bulabilme konuların yer aldığı video veya etileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
