INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '6', '4. KUR’AN-I KERİM’İ OKUYORUM', 'KK.6.4.5. Nas suresini ezberleyebilme', 'Nas suresini kurallarına uygun bir şekilde okunduğu bir video, etkileşimli içerik veya ses kaydı hazırlanır.', 'Video/Etkileşimli İçerik/Ses', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '7', 'KUR’AN-I KERİM’İ TANIYALIM', '1. Kur''an-ı Kerim''in İslam Dininin ana kaynağı olduğunu kavrar.', '* Kur''an-ı Kerim''in İslam dininin temel kaynağı olduğu, dinin anlaşılmasında Kur’an’ın önemi, Kur’an-ı Kerim’in inanç, ibadet ve ahlaka yönelik açıklamaları vb. konuların ayet ve hadislerle açıklandığı video, animasyon veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '7', 'KUR’AN-I KERİM’İ TANIYALIM', '2. Kur''an-ı Kerim''in insan hayatının bütün yönlerine ilişkin öğretisi bulunduğunu fark eder.', '* Allah (c.c.) tarafından halife olarak yaratılan insanın, O’nun tarafından rehbersiz bırakılmayarak peygamberler ve ilahi kitaplar gönderdiği ve bu bağlamda Kur’an-ı Kerim’in insanı hem dünya hem de ahiret hayatında mutlu kılmak için bir hayat rehberi olduğu ve kıyamete kadar kılavuzluk yapacağı vb. konularla ilgili video veya animasyon hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '7', 'KUR’AN-I KERİM’İ TANIYALIM', '3. Hz. Davud, Hz. Süleyman kıssalarını temel özellikleriyle açıklar.', '* Hz. Süleyman ile ilgili ayet ve hadislerin referans alındığı; Hz. Süleyman’ın Hz. Davud’un oğlu olduğu ve babası gibi hem güçlü bir hükümdar hem de peygamber olduğu, sorunları çözmedeki başarısı, adalet ile ün salmış olması, ona verilen çeşitli nimetler ve hayvanların dilini bilmesi, Belkıs kıssası vb. konuların yer aldığı ve bu konuların hayatla ilişkisini gösteren video veya animasyon hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '7', 'KUR’AN-I KERİM’İ TANIYALIM', '4. İbrahim suresini genel özellikleriyle tanır.', '* Hz. İbrahim’in şahsi özellikleri, Hz. Muhammed’in (s.a.v.) atası olduğu; İbrahim suresinde yer alan Allah’ın (c.c.) vahiy ve peygamber göndermesinin sebepleri ve önemi, vahyi anlamanın önemi, peygamberlerin tebliği karşısında inanmayanların gösterdikleri olumsuz davranışlar ve sebepleri; surede detaylı olarak ele alınan Hz. İbrahim’in yüksek dinî kişiliği, Kâbe''yi inşa etmesi, tevhid mücadelesi, dua örnekleri vb. konuların ve bunların hayatla ilişkisini gösteren sunu, video veya animasyon hazırlanır.', 'Video/Sunu', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '7', 'KUR’AN-I KERİM’İ TANIYALIM', '5. İsra suresinin 24. ayetinde yer alan duanın anlamını kavrar.', '* İsra suresinin 24. ayetinde yer alan anne-baba sevgisi, anne-babaya karşı görevler, onlara nasıl davranılması gerektiği, anne-baba için dua edilmesi vb. konuların ve ilgili ayetin mealinin hayatla ilişkisini gösteren sunu, video, animasyon veya etkileşimli içerik hazırlanır.', 'Video/Sunu/Etkile şimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '7', 'KUR’AN-I KERİM’İ GÜZEL OKUMA', '1. Okunuşla ilgili bazı harf ve işaretlere dikkat ederek Kur’an’ı okur.
