INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '8', 'KUR’AN-I KERİM’İ GÜZEL OKUMA', '3. Kur''an-ı Kerim''i okurken, İdgam-ı Mea''l-gunne, İdgam- ı Bila-gunne, Sakin Mim''in okunuşu, Ra''nın hükümleri''ni ayırt eder.', '* İdgam, idgamlar (idgam-ı mea’l- gunne,idgam-ı bila-gunne) konularının ve örneklerinin yer aldığı video, animasyon veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '8', 'KUR’AN-I KERİM’İ GÜZEL OKUMA', '3. Kur''an-ı Kerim''i okurken, İdgam-ı Mea''l-gunne, İdgam- ı Bila-gunne, Sakin Mim''in okunuşu, Ra''nın hükümleri''ni ayırt eder.', '* İdgam-ı mea’l-gunne kuralının tanıtımı ve bu kuralla ilgili örneklerin yer aldığı video veya animasyon hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '8', 'KUR’AN-I KERİM’İ GÜZEL OKUMA', '3. Kur''an-ı Kerim''i okurken, İdgam-ı Mea''l-gunne, İdgam- ı Bila-gunne, Sakin Mim''in okunuşu, Ra''nın hükümleri''ni ayırt eder.', '* İdgam-ı bila-gunne kuralının tanıtımı ve bu kuralla ilgili örneklerin yer aldığı video veya animasyon hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '8', 'KUR’AN-I KERİM’İ GÜZEL OKUMA', '3. Kur''an-ı Kerim''i okurken, İdgam-ı Mea''l-gunne, İdgam- ı Bila-gunne, Sakin Mim''in okunuşu, Ra''nın hükümleri''ni ayırt eder.', '* İdgamlar, idgam-ı mea’l-gunne, idgam-ı bila-gunne konularının yer aldığı ve bunlara ait örneklerin bulunduğu video veya animasyon hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '8', 'KUR’AN-I KERİM’İ GÜZEL OKUMA', '3. Kur''an-ı Kerim''i okurken, İdgam-ı Mea''l-gunne, İdgam- ı Bila-gunne, Sakin Mim''in okunuşu, Ra''nın hükümleri''ni ayırt eder.', '* Sakin mim ile sakin mim’in okunuş şekilleriyle ilgili video veya animasyon hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '8', 'KUR’AN-I KERİM’İ GÜZEL OKUMA', '4. Vakıf, İbtida, Vasıl ve İşaretleri''ni tanır.', '* İbtida,  Vasıl ve Vakıf le ilgili bilgilerin ve uygulamasının örneklerle ele alındığı video veya animasyon hazırlanır.
