INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '9', '1. KUR’AN-I KERİM’İ TANIYORUM', 'Köprü Kurma', 'Kur’an-ı Kerim okumanın önemi ve fazileti hakkında eğitsel bir video hazırlanır.', 'Video', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '9', '2. KUR’AN-I KERİM’İ GÜZEL OKUMA KURALLARI: KUR’AN OKUMAYA GİRİŞ', 'Köprü Kurma', 'Kur’an-ı Kerim’in güzel okuma kurallarına uygun olarak okunduğu video veya etkileşimli video hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '9', '2. KUR’AN-I KERİM’İ GÜZEL OKUMA KURALLARI: KUR’AN OKUMAYA GİRİŞ', 'KK.9.2.1. Harfleri seslendirebilme', 'Kur’an-ı Kerim alfabesindeki harfleri ve bu harflerin isimlerini tanıtan video veya etkileşimli video hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '9', '2. KUR’AN-I KERİM’İ GÜZEL OKUMA KURALLARI: KUR’AN OKUMAYA GİRİŞ', 'KK.9.2.1. Harfleri seslendirebilme', 'Harflerin mahreç yerleri, ağız ve ses yoluyla çıkış bölgelerini yansıtan görseller içeren video, etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '9', '2. KUR’AN-I KERİM’İ GÜZEL OKUMA KURALLARI: KUR’AN OKUMAYA GİRİŞ', 'KK.9.2.1. Harfleri seslendirebilme', 'Harflerin başta, ortada ve sonda yazılışlarını gösteren video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '9', '2. KUR’AN-I KERİM’İ GÜZEL OKUMA KURALLARI: KUR’AN OKUMAYA GİRİŞ', 'KK.9.2.2. Harfleri harekeler ile seslendirebilme', 'Harekelerin (üstün, esre, ötre) harflerle birleşerek nasıl telaffuz edildiğini gösteren video veya etkileşimli video hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '9', '2. KUR’AN-I KERİM’İ GÜZEL OKUMA KURALLARI: KUR’AN OKUMAYA GİRİŞ', 'KK.9.2.3. Okunuşla ilgili özel durumları seslendirebilme', 'Kur’an-ı Kerim’in güzel okuma kurallarına uygun olarak okunduğu bir video veya etkileşimli video hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '9', '2. KUR’AN-I KERİM’İ GÜZEL OKUMA KURALLARI: KUR’AN OKUMAYA GİRİŞ', 'KK.9.2.3. Okunuşla ilgili özel durumları seslendirebilme', 'Kelimeler ve okunuşlarının bulunduğu eşleştirme etkinliği hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '9', '2. KUR’AN-I KERİM’İ GÜZEL OKUMA KURALLARI: KUR’AN OKUMAYA GİRİŞ', 'KK.9.2.4. Med (uzatma) ve çeşitlerini seslendirebilme', 'Med ve çeşitleri ile ilgili örnek kelimelerin yer aldığı video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '9', '3. KUR’AN-I KERİM’İ OKUYORUM', 'KK.9.3.1. Al-i İmran suresinin 1-15. sayfalarını (1-115. ayetler) ve Yasin suresinin 1-6. sayfalarını (1-83. ayetler) usulüne uygun yüzünden okuyabilme', 'Al-i İmran suresinin 1-15. sayfalarını öğrencilerin tecvitli olarak dinleyebilecekleri, takip edebilecekleri video veya etkileşimli video hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '9', '3. KUR’AN-I KERİM’İ OKUYORUM', 'KK.9.3.1. Al-i İmran suresinin 1-15. sayfalarını (1-115. ayetler) ve Yasin suresinin 1-6. sayfalarını (1-83. ayetler) usulüne uygun yüzünden okuyabilme', 'Yasin suresinin 1-6. sayfalarını öğrencilerin tecvitli olarak dinleyebilecekleri, takip edebilecekleri video veya etkileşimli video hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '9', '4. KUR’AN-I KERİM’DEN SURELER VE AYETLER EZBERLİYORUM', 'KK.9.4.1. Fatiha, Kevser, İhlas, Fil ve Kureyş surelerinin anlamlarını özetleyebilme', 'Fatiha suresinin Arapça metnini, Türkçe mealini ve tefsirini içeren etkileşimli içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
