INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '10', '1. KUR’AN-I KERİM’İ TANIYORUM', 'KK.10.1.4. Taha suresinin 25-28. ayetlerinde yer alan duayı çözümleyebilme.', 'Duanın önemi ve ilgili ayetlerdeki duanın nerelerde yapılacağının anlatıldığı video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '10', '2. KUR’AN-I KERİM’İ GÜZEL OKUMA KURALLARI: TECVİT', 'Köprü kurma', 'Tecvid kurallarının uygulandığı Kuran-Kerim''i güzel okuma örneklerinin olduğu video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '10', '2. KUR’AN-I KERİM’İ GÜZEL OKUMA KURALLARI: TECVİT', 'KK.10.2.1. Med çeşitleriyle ilgili örnekleri seslendirebilme.', 'Medlerin seslendirildiği video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '10', '2. KUR’AN-I KERİM’İ GÜZEL OKUMA KURALLARI: TECVİT', 'KK.10.2.2. Tenvin ve sakin nunun hâlleri ile ilgili konuları çözümleyebilme.', 'Tenvin ve sakin nunun hâlleri ile ilgili örneklerin seslendirildiği video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '10', '2. KUR’AN-I KERİM’İ GÜZEL OKUMA KURALLARI: TECVİT', 'KK.10.2.3. İdgam çeşitlerini sınıflandırabilme.', 'İdgam çeşitlerini içeren örneklerin seslendirildiği video veya etkileşimli içerik hazırlanır', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '10', '3. KUR’AN-I KERİM’İ OKUYORUM', 'KK.10.3.1. Al-i İmran suresinin 16-27. sayfalarını (116- 200. ayetler)  ve Hac suresinin 1-10. sayfalarını (1- 78.ayetler) usulüne uygun yüzünden okuyabilme.', 'Al-i İmran suresinin 16-27. sayfalarını öğrencilerin tecvitli olarak dinleyebilecekleri, takip edebilecekleri video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '10', '3. KUR’AN-I KERİM’İ OKUYORUM', 'KK.10.3.1. Al-i İmran suresinin 16-27. sayfalarını (116- 200. ayetler)  ve Hac suresinin 1-10. sayfalarını (1- 78.ayetler) usulüne uygun yüzünden okuyabilme.', 'Hac suresinin 1-10. sayfalarını öğrencilerin tecvitli olarak dinleyebilecekleri, takip edebilecekleri video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '10', '4. KUR’AN-I KERİM’DEN SURELER VE AYETLER EZBERLİYORUM', 'Köprü kurma', 'İçerik çerçevesindeki surelerden en az birini içeren video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '10', '4. KUR’AN-I KERİM’DEN SURELER VE AYETLER EZBERLİYORUM', 'KK.10.4.1.  Maun, Kâfirun, Nasr sureleri ve Ayetelkürsi''nin anlamlarını özetleyebilme.', 'Maun suresinin metnini, Türkçe mealini ve tefsirini içeren etkileşimli içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '10', '4. KUR’AN-I KERİM’DEN SURELER VE AYETLER EZBERLİYORUM', 'KK.10.4.1.  Maun, Kâfirun, Nasr sureleri ve Ayetelkürsi''nin anlamlarını özetleyebilme.', 'Kâfirun suresinin metnini, Türkçe mealini ve tefsirini içeren etkileşimli içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
