INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '10', '4. KUR’AN-I KERİM’DEN SURELER VE AYETLER EZBERLİYORUM', 'KK.10.4.2. Maun, Kâfirun, Nasr sureleri ve Ayetelkürsi''yi ezberleyebilme.', 'Maun suresinin okunuşunun ayet ayet ve bütün olarak verildiği video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '10', '4. KUR’AN-I KERİM’DEN SURELER VE AYETLER EZBERLİYORUM', 'KK.10.4.2. Maun, Kâfirun, Nasr sureleri ve Ayetelkürsi''yi ezberleyebilme.', 'Kafirun suresinin okunuşunun ayet ayet ve bütün olarak verildiği video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
