INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '10', '4. KUR’AN-I KERİM’DEN SURELER VE AYETLER EZBERLİYORUM', 'KK.10.4.2. Maun, Kâfirun, Nasr sureleri ve Ayetelkürsi''yi ezberleyebilme.', 'Ayetelkürsi’nin okunuşunun ayet ayet ve bütün olarak verildiği video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '11', 'KUR’AN-I KERİM’İ TANIYALIM', '6. Hz. Musa''nın kıssasını ve İsrailoğulları''nın toplumsal özelliklerini ana hatlarıyla kavrar.', '* Hz. Musa’nın Hz. İbrahim’e dayanan soyu, Kur’an’da en çok bahsedilen peygamberin Hz. Musa olduğu; Hz. Musa’nın doğumu, çocukluğu, gençliği ve peygamberliği; Hz. Şuayb’ın kızıyla evlenmesi, Firavun ile mücadelesi, On Emir, Hz. Harun, İsrailoğullarının yaşadıkları vb. konuların ele alındığı video, animasyon, sunu veya etkileşimli içerik hazırlanır.', 'Video/Sunu/Etkile şimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '11', 'KUR’AN-I KERİM’İ TANIYALIM', '7. Yasin suresini genel özellikleriyle tanır.', '* Yasin suresinin tanıtımının yapıldığı, Yasin suresinin fazileti ve Kur’an-ı Kerim’in kalbi olduğu, surenin Mekke döneminde indiği, surede tevhid, nübüvvet ve ahiret konularının ağırlıklı olarak işlendiği, bu surenin ölülerin arkasından okunma sebebi vb. konuların ve bunların hayatla ilişkisini ele alan video, animasyon, sunu veya etkileşimli içerik hazırlanır.', 'Video/Sunu/Etkile şimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '11', 'KUR’AN-I KERİM’İ TANIYALIM', '8. Ahkaf suresinin 15. ayetinde yer alan duanın anlamını kavrar.', '* Ahkaf suresinin 15. ayetinde yer alan anne-babaya iyi davranılması, anne sevgisinin önemi, anne- babaya karşı görevler, anne-baba ve çocuklar için dua edilmesi vb. konuların ve ilgili ayetteki mesajların hayatla ilişkisini gösteren video, animasyon sunu veya etkileşimli içerik hazırlanır.', 'Video/Sunu/Etkile şimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '11', 'KUR’AN-I KERİM OKUMA VE EZBERLEME', '1. Okunuşla ilgili bazı harf ve işaretlere dikkat ederek Kur''an''ı okur.
