INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '11', 'KUR’AN-I KERİM OKUMA VE EZBERLEME', '3.  Kur''an-ı Kerim''i okurken, İhfa, izhar, İklab, İdgam-ı Mea''l-gunne, İdgam-ı Bila-gunne, İdgam-ı Misleyn, İdgam-ı Mütecaniseyn, İdgam-ı Mütekaribeyn, İdgam-ı Şemsiye, İzhar-ı Kameriye''yi ayırt eder.', '* Tenvin ve sakin nun, ihfa, izhar, iklab ve idgamlar (idgam-ı mea''l-gunne, idgam-ı bila-gunne, idgam-ı misleyn, idgam-ı mütecaniseyn, idgam-ı mütekaribeyn, idgam-ı şemsiye, izhar-ı kameriye''yi) kuralları ile bunlara ait örneklerin yer aldığı video veya animasyon hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '11', 'KUR’AN-I KERİM OKUMA VE EZBERLEME', '4. Ezberlenecek sureleri usulüne uygun olarak ezberler.
