INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '12', 'KUR’AN-I KERİM’İ TANIYALIM', '1. Kur''an-ı Kerim''i anlamada Hz. Peygamber''in sünnetinin yeri ve önemini fark eder.', '* İslam dinindeki temel kaynakların Kur’an ve Hz. Peygamber''in sünnetinin olduğu, Hz. Peygamber’in görevleri (tebliğ, tebyin, temsil), sünnet kavramı, Kur’an’ı anlamak için Hz. Peygamber’e olan ihtiyaç, Hz. Peygamber''in sünnetinin Kur’an’ın anlaşılıp hayata geçirilmesindeki yerinin örneklerle ortaya konulması vb. konuların ele alındığı sunu, video, animasyon veya etkileşimli içerik hazırlanır.', 'Video/Sunu/Etkile şimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '12', 'KUR’AN-I KERİM’İ TANIYALIM', '5. Hz. İsa''nın gönderildiği toplumun özelliklerini ve sonrasındaki değişimleri kavrar.', '* Hz. İsa, Hz. Meryem, Beyt-i Makdis, Hz. İsa’nın babasız olarak dünyaya gelme mucizesi (Hz. Âdem’in de annesinin ve babasının olmadığı),   Hz. İsa’nın beşikte iken konuşması, İsrailoğullarına peygamber olarak gönderilmesi ve havârîler, tevhid dinini tebliğ etmesi, Hz. İsa’ya verilen mucizeler, İncil kitabının verilmesi ve İncil’in daha sonra tahrif edilmesi, Yahudilerin Hz. İsa’ya ihaneti vb. temel mesajlara vurgu yapıldığı ve bunların hayatla ilişkisinin kurulduğu sunu, video, animasyon veya etkileşimli içerik hazırlanır.', 'Video/Sunu/Etkile şimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '12', 'KUR’AN-I KERİM’İ TANIYALIM', '6. Mülk ve Nebe surelerini genel özellikleriyle tanır.', '* Mülk suresinin tanıtımı, isminin ne anlama geldiği, Mülk suresinin fazileti, surenin Mekke döneminde indiği, surede Yüce Allah’ın varlığı ve birliği, evrendeki mutlak egemenliği, hayatın ve ölümün var ediliş gayesi, öldükten sonra dirilme ve kendilerine gelen ilahî uyarıları dikkate almayanların karşılaşacakları kötü son konularının ağırlıklı olarak işlendiği, vb. konuların ve bunların hayatla ilişkisini ele alan etkileşimli içerik, sunu, video veya animasyon hazırlanır.', 'Video/Sunu/Etkile şimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '12', 'KUR’AN-I KERİM’İ TANIYALIM', '6. Mülk ve Nebe surelerini genel özellikleriyle tanır.', '* Nebe suresinin tanıtımı, isminin ne anlama geldiği, nerede indirildiği, surede ağırlıklı olarak ele alınan kıyamet, öldükten sonra dirilme, hesap, ceza ve mükâfat, Allah’ın varlık ve kudretini gösteren deliller, melekler vb. konuların hayatla ilişkisini gösteren etkileşimli içerik, sunu,video veya animasyon hazırlanır.', 'Video/Sunu/Etkile şimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '12', 'KUR’AN-I KERİM’İ TANIYALIM', '7. Araf suresinin 151. ayetinde yer alan duanın anlamını kavrar.', '* Araf suresinin 151. ayeti bağlamında dua ve dua etmenin adabı, dua etmenin önemi, duanın Kur’an’ın değindiği en temel konulardan olduğu, Kur’an’ın örnek dualarla da bize rehberlik ettiği, ayetteki duanın ve mesajının hayatla ilişkisini gösteren sunu, video veya animasyon hazırlanır.', 'Video/Sunu', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '12', 'KUR’AN-I KERİM’İ GÜZEL OKUMA', '1. Okunuşla ilgili bazı harf ve işaretlere dikkat ederek Kur’an’ı okur.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '12', 'KUR’AN-I KERİM’İ GÜZEL OKUMA', '1. Okunuşla ilgili bazı harf ve işaretlere dikkat ederek Kur’an’ı okur.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '12', 'KUR’AN-I KERİM’İ GÜZEL OKUMA', '1. Okunuşla ilgili bazı harf ve işaretlere dikkat ederek Kur’an’ı okur.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '12', 'KUR’AN’I KERİM’İ GÜZEL OKUMA', '3. Kur''an-ı Kerim''i okurken, Sakin Mim''in okunuşu, Ra’nın hükümleri, Lafzatullah’taki lam harfinin okunuşunu ve Sekte’yi ayırt eder.', '* Sakin mim ile sakin mim’in okunuş şekilleriyle ilgili video, animasyon veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '12', 'KUR’AN’I KERİM’İ GÜZEL OKUMA', '3. Kur''an-ı Kerim''i okurken, Sakin Mim''in okunuşu, Ra’nın hükümleri, Lafzatullah’taki lam harfinin okunuşunu ve Sekte’yi ayırt eder.', '* Kalkale kuralının tanıtımı ve bu kuralla ilgili örneklerin yer aldığı video veya animasyon hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '12', 'KUR’AN’I KERİM’İ GÜZEL OKUMA', '3. Kur''an-ı Kerim''i okurken, Sakin Mim''in okunuşu, Ra’nın hükümleri, Lafzatullah’taki lam harfinin okunuşunu ve Sekte’yi ayırt eder.', '* Ra harfinin üç şekilde okunmasının ele alındığı ve ra’nın hükümleriyle ilgili örneklerin yer aldığı video veya animasyon hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '12', 'KUR’AN’I KERİM’İ GÜZEL OKUMA', '3. Kur''an-ı Kerim''i okurken, Sakin Mim''in okunuşu, Ra’nın hükümleri, Lafzatullah’taki lam harfinin okunuşunu ve Sekte’yi ayırt eder.', '* Lafzatullah’taki lam harfinin ince ve kalın okunduğu yerlerin ve örneklerin bulunduğu video veya animasyon hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '12', 'KUR’AN’I KERİM’İ GÜZEL OKUMA', '3. Kur''an-ı Kerim''i okurken, Sakin Mim''in okunuşu, Ra’nın hükümleri, Lafzatullah’taki lam harfinin okunuşunu ve Sekte’yi ayırt eder.', '* Sekte kuralının tanıtımının, sekte olan yerlerin ve bu kuralın uygulanışının ele alındığı video, animasyon veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '12', 'KUR’AN’I KERİM’İ GÜZEL OKUMA', '4. Vakıf, İbtida, Vasıl ve İşaretleri''ni tanır.', '* Vakıf, ibtida ve vasıl kavramları ve uygulamalarının yer aldığı video veya animasyon hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '12', 'KUR’AN’I KERİM’İ GÜZEL OKUMA', '5. Secde ayetlerini ve secde işaretlerini tanır.', '* Secde ayeti, secde işareti, secde ayetlerinin genel olarak içerdiği konular,  tilavet secdesinin hangi durumlarda yapılması gerektiği, tilavet secdesinin yapılışı, secde ayetlerinin yer aldığı sure ve ayetler vb. konularda video, animasyon veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '12', 'KUR’AN-I KERİM’İ GÜZEL OKUMA', '6. Ezberlenecek sureleri usulüne uygun olarak ezberler.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Kuran-ı Kerim', '12', 'KUR’AN-I KERİM’İ GÜZEL OKUMA', '6. Ezberlenecek sureleri usulüne uygun olarak ezberler.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '5', '1. ÇOCUKLUK YILLARINDA PEYGAMBERİMİZ', 'PH.5.1.1. Peygamberimizin doğduğu dönemi etkileyen Fil Vakası’na ilişkin bilgileri toplayabilme', 'Fil Vakası'' nın ne olduğunu, neden gerçekleştiğini, sonuçlarını ve Peygamberimiz''in doğduğu dönemi nasıl etkilediğini, Kâbe''nin o dönemdeki önemini, Ebrehe''nin amacı ve fillerin neden Kâbe''ye saldırmadığını anlatan video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '5', '1. ÇOCUKLUK YILLARINDA PEYGAMBERİMİZ', 'PH.5.1.2. Peygamberimizin çocukluk yıllarında aile büyükleriyle kurduğu ilişkileri çözümleyebilme', 'Peygamberimizin çocukluk yıllarında yaşadığı Mekke, Medine ve Sadoğulları yurdu gibi yerleri gösteren, Peygamberimizin çocukluk yıllarında aile büyükleriyle yaşadığı önemli olayları anlatan, Peygamberimizin aile büyüklerine karşı gösterdiği saygı, sevgi ve sorumluluk bilincini vurgulayan video veya etkileşimli içerik hazırlanır.', NULL, NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '5', '2. PEYGAMBERİMİZİN ÇEVRESİNDEKİ ÇOCUKLAR', 'PH.5.2.1. Peygamberimizin çocuklarla kurduğu iletişimi sentezleyebilme', 'Hz. Peygamber''in çocuklarla olan etkileşimlerini, onlara karşı şefkatini, öğreticiliğini ve oyun oynamasını konu alan, çocukların Hz. Peygamber''in sıcak ve samimi yaklaşımını anlamalarına yardımcı olacak video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '5', '2. PEYGAMBERİMİZİN ÇEVRESİNDEKİ ÇOCUKLAR', 'PH.5.2.2. Asrısaadet’teki çocukların Peygamberimize olan sevgisini çözümleyebilme', 'Asr-ı Saadet dönemindeki çocukların Hz. Peygamber''e olan sevgisini konu alan çocukların Hz. Peygamber''e olan sevgisini ve saygısını ifade eden sözlerini, davranışlarını, olayları ve ilgili kıssaları içeren video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '5', '2. PEYGAMBERİMİZİN ÇEVRESİNDEKİ ÇOCUKLAR', 'PH.5.2.3. Peygamberimizin yanında yetişen çocukların onu rehber edinmeleri konusunda sorgulamalar yapabilme', 'Hz. Muhammed''in çocuklarla olan etkileşimlerini ve onlara verdiği öğütleri anlatan video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '5', '3. REHBERİM PEYGAMBERİM', 'PH.5.3.1. Peygamberimizin temizliğe verdiği öneme dair akıl yürütebilme', 'Peygamberimizin temizlik anlayışını beden, çevre ve manevi temizlik olarak üç ana başlıkta ele alan, her başlık altında ilgili hadisleri ve açıklamaları içeren içerik hazırlanır.', NULL, NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '5', '3. REHBERİM PEYGAMBERİM', 'PH.5.3.2. Peygamberimizin yeme içme ve giyim kuşam adabına yönelik ilkeleri yapılan-dırabilme', 'Peygamberimizin yeme-içme ve giyim kuşam adabını anlatan, günümüzdeki uygulamalarına örnekler veren israfın önlenmesi, mütevazı yaşamın önemi, helal gıdaya dikkat edilmesi gibi konulara yer veren video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '5', '4. AİLE HAYATINDA PEYGAMBERİMİZ', 'PH.5.4.1. Peygamberimizin aile hayatına dair akıl yürütebilme', 'Peygamberimizin (s.a.v.) aile bireylerini, onların isimlerini, Peygamberimizle olan ilişkilerini ve kısaca hayat hikâyelerini gösteren etkileşimli bir aile ağacı hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '6', '1. GENÇLİK YILLARINDA PEYGAMBERİMİZ', 'PH.6.1.1. Peygamberimizin gençlik yıllarındaki ahlaki özelliklerini yorumlayabilme', 'H.z. Peygamber''in gençlik yıllarına ait önemli olayları göstermek için etkileşimli zaman çizelgesi hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '6', '1. GENÇLİK YILLARINDA PEYGAMBERİMİZ', 'PH.6.1.2. Pegamberimizin Hira’daki tefekkür sürecini çözümleyebilme', 'Peygamberimizin Hira da tefekkürle geçirdiği günlere yönelik içerik hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '6', '2. PEYGAMBERİMİZİN ÇEVRESİNDEKİ GENÇLER', 'PH.6.2.1. Peygamberimizin gençlerle iletişiminde onlara verdiği değeri sentezleyebilme', 'Peygamberimizin gençlerle iletişiminde onlara verdiği değere yönelik içerik hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '6', '2. PEYGAMBERİMİZİN ÇEVRESİNDEKİ GENÇLER', 'PH.6.2.2. Asrısaadet’teki gençlerin Peygamberimize duyduğu sevgiyi çözümleyebilme', 'Şehadet kavramına yönelik frayer tekniği uygulanarak etkileşimli içerik hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '6', '2. PEYGAMBERİMİZİN ÇEVRESİNDEKİ GENÇLER', 'PH.6.2.3. Peygamberimizin yanında yetişen gençlerin onu rehber edinmeleri konusunda
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '6', '3. REHBERİM PEYGAMBERİM', 'PH.6.3.1. Peygamberimizin Allah’a (cc) bağlılığı konusunu çözümleyebilme', 'H.z. Peygamberin hayatından örnek hadis ve Kuran-ı Kerimden örnek ayetler ile Peygamberimizin Allah''a olan bağlılığını anlatmaya yönelik içerik hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '6', '3. REHBERİM PEYGAMBERİM', 'PH.6.3.3. Yüce Allah’a şükretmenin Peygamberimizin hayatındaki yerini yorumlayabilme', 'Şükür  için secde etmek, üzüntülü durumlarda da sabrederek hamt etmeye yönelik örnek hadislerden oluşan içerik hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '6', '4. TOPLUMSAL HAYATTA PEYGAMBERİMİZ', 'PH.6.4.1. Peygamberimizin toplumsal ilişkilerini sentezleyebilme', 'Sılayırahim, akraba, komşu, vefa ve çevre kavramlarına yönelik içerik hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '6', '4. TOPLUMSAL HAYATTA PEYGAMBERİMİZ', 'PH.6.4.2. Peygamberimizin çevreye karşı gösterdiği duyarlılığı çözümleyebilme', 'Çevre bilincini oluşturan parçalardan doğanın ekolojik dengesinin korunması, çevrenin temiz tutulması, doğal kaynakların tasarruflu kullanılması, canlıların hayat hakkına saygı gösterilmesi ile ilgili içerik hazırlanır.', NULL, NULL);
