INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '7', '5. ÜNİTE: SAHABEDEN ÖRNEK DAVRANIŞLAR', '1. Sahabenin cesaretini ve sadakatini örneklerle açıklar.', '* Sahabenin cesaret ve sadakati hakkında video, eşleştirme, boşluk doldurma, sıralama, doğru yanlış, çoklu seçim, bulmaca, oyun veya benzeri etkileşimli içerik hazırlanır.', NULL, NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '7', '5. ÜNİTE: SAHABEDEN ÖRNEK DAVRANIŞLAR', '2. Sahabenin yardımlaşma ve dayanışmasına örnekler verir.', '* Sahabenin yardımlaşma ve dayanışması hakkında video, eşleştirme, boşluk doldurma, sıralama, doğru yanlış, çoklu seçim, bulmaca, oyun veya benzeri etkileşimli içerik hazırlanır.', NULL, NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '7', '5. ÜNİTE: SAHABEDEN ÖRNEK DAVRANIŞLAR', '3. Sahabenin azim ve fedakârlıklarından örnekler verir.', '* Sahabenin azmi ve fedakârlığı hakkında video, eşleştirme, boşluk doldurma, sıralama, doğru yanlış, çoklu seçim, bulmaca, oyun veya benzeri etkileşimli içerik hazırlanır.', NULL, NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '7', '5. ÜNİTE:
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '8', '1. PEYGAMBERİMİZİN MEKKE YILLARI', '8.1.2. Peygamberimizin Mekke yıllarını ve Yesrib’e hicreti ana hatlarıyla açıklar.', '* Peygamberimizin hicret yolcuğunu gösteren video veya infografik hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '8', '1. PEYGAMBERİMİZİN MEDİNE YILLARI', '8.1.3. Peygamberimizin Medine’de yaptığı toplumsal faaliyetleri kavrar.', '* Peygamberimizin katılmış olduğu faaliyetleri gösteren video veya animasyon hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '8', '1. PEYGAMBERİMİZİN MEDİNE YILLARI', '8.1.4. Müşriklerle münasebetleri sebep ve sonuçları açısından değerlendirir.', '* Peygamberimizin gazvelerini anlatan, bu gazvelerin yapıldığı yerlerin günümüzdeki görüntülerine yer verilen sunu, video veya animasyon düzenlenir.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '8', '1. PEYGAMBERİMİZİN MEDİNE YILLARI', '8.1.5. Peygamberimizin diğer din mensuplarıyla ilişkilerinin dayandığı ilkeleri kavrar.', '* Peygamberimizin İslam’a davet mektuplarının yer aldığı infografik veya video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '8', '1. PEYGAMBERİMİZİN MEDİNE YILLARI', '8.1.6. Mekke’nin fethini sebep ve sonuçları açısından değerlendirir.', '* Hudeybiye Antlaşması’nın maddelerini anlatan infografik veya video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '8', '1. PEYGAMBERİMİZİN MEDİNE YILLARI', '8.1.7. Veda Haccı’nın önemini ve Veda Hutbesi’ndeki evrensel mesajları yorumlar.', '* Konuyla ilgili ilgili video, eşleştirme, boşluk doldurma, sıralama, doğru yanlış, çoklu seçim, bulmaca, oyun veya benzeri etkileşimli içerik hazırlanır.', NULL, NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '8', '1. PEYGAMBERİMİZİN MEDİNE YILLARI', '8.1.8. Müslümanların kardeşliği ile ilgili bir hadisi yorumlar.', '* Müslümanların kardeşliği ile ilgili bir hadisin seslendirildiği ses dosyası hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '8', '2. PEYGAMBERİMİZİN DİNDEKİ KONUMU', '8.2.1. Peygamberimizin dindeki konumunu ayet ve hadislerle açıklar.', '* Peygamberimizin tebliğ görevine değinen ayet ve hadislerin seslendirildiği ses dosyası hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '8', '2. PEYGAMBERİMİZİN DİNDEKİ KONUMU', '8.2.2. Peygamberimizin nebevi uygulamalarına örnekler verir.', '* Peygamberimizin nebevi uygulamalarına değinen hadis ve rivayetlerin seslendirildiği ses dosyası hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '8', '2. PEYGAMBERİMİZİN DİNDEKİ KONUMU', '8.2.2. Peygamberimizin nebevi uygulamalarına örnekler verir.', '* Peygamberimizin nebevi uygulamalarına değinen hadis ve rivayetlerin seslendirildiği ses dosyası hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '8', '2. PEYGAMBERİMİZİN DİNDEKİ KONUMU', '8.2.1. Peygamberimizin dindeki konumunu ayet ve hadislerle açıklar.', '* Peygamberimizin teşri yetkisine değinen ayet ve hadislerin seslendirildiği ses dosyası hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '8', '2. PEYGAMBERİMİZİN DİNDEKİ KONUMU', '8.2.3. Peygambere itaat ile ilgili bir hadisi yorumlar.', '* Peygambere itaatle ilgili bir hadisin anlamı ve mesajı üzerinde durulan video veya animasyon hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '8', '3. PEYGAMBERİMİZ VE SOSYAL HAYAT', '8.3.1. Peygamberimizin adaleti sağlamada ortaya koyduğu ilkeleri örneklerle açıklar.', '* Peygamberimizin adaleti sağlamada ortaya koyduğu ilkelerle ilgili eşleştirme, boşluk doldurma, sıralama, doğru yanlış, çoklu seçim, bulmaca, oyun veya benzeri etkileşimli içerik hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '8', '3. PEYGAMBERİMİZ VE SOSYAL HAYAT', '8.3.3. Peygamberimizin çalışma hayatı ile ilgili ilkelerini benimser.', '* Helal kazanç, çalışana insanca davranma ve emeklerinin karşılığını tam olarak verme, görevini hakkıyla yerine getirme, üretken olma, kendi emeğiyle geçinme gibi konularla ilgili video veya animasyon hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '8', '3. PEYGAMBERİMİZ VE SOSYAL HAYAT', '8.2.4. Peygamberimizi örnek alarak çevreye karşı sorumluluklarını fark eder.', '* Çevreye karşı sorumluluklarımızla ilgili video, animasyon veya etkileşimli içerik hazırlanır.', NULL, NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '8', '3. PEYGAMBERİMİZ VE SOSYAL HAYAT', '8.3.5. Peygamberimizin tabiat sevgisi ile ilgili bir hadisini yorumlar.', '* Peygamberimizin tabiat sevgisi ile ilgili bir hadisin seslendirildiği ses dosyası hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '8', '4. EN GÜZEL ÖRNEK PEYGAMBERİMİZ', '8.4.1. Peygamberimizin hayatındaki dünya ve ahiret dengesi ile ilgili örnekler verir.', '* Peygamberimizin hayatında dünya ve ahiret dengesi ile ilgili hadislerin seslendirildiği ses dosyası hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '8', '4. EN GÜZEL ÖRNEK PEYGAMBERİMİZ', '8.4.5. Salih amel ile ilgili bir hadisi yorumlar.', '* Salih amel ile ilgili bir hadisin seslendirildiği ses dosyası hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '8', '5. İSLAM DAVETİ VE SAHABE', '8.5.1. İslam dininin yayılmasında sahabenin rolünü kavrar.', '* İslam''ın yayılışını gösteren infografik hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '8', '5. İSLAM DAVETİ VE SAHABE', '8.5.3. Bazı âlim ve şehit sahabilerin örnek vasıflarını benimser.', '* Bazı âlim sahabilerin hayatlarının anlatıldığı video veya animasyon hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '8', '5. İSLAM DAVETİ VE SAHABE', '8.5.3. Bazı âlim ve şehit sahabilerin örnek vasıflarını benimser.', '* Bazı şehit sahabilerin hayatlarının anlatıldığı video veya animasyon hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '8', '5. İSLAM DAVETİ VE SAHABE', '8.5.4. İlim ile ilgili bir hadisi yorumlar.', '* İlim ile ilgili bir hadisin açıklamasını içeren video veya animasyon hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '9', '1. PEYGAMBERİMİZİN HAYATINDA ÖNEMLİ BİR ŞEHİR: MEKKE', 'PH.9.1.1. İslam’dan önce Mekke’de gerçekleşen önemli olayları çözümleyebilme', 'Hz. İsmail ve Hz. Hacer''in Mekke''ye gelişini, Kâbe''nin inşasını içeren video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '9', '1. PEYGAMBERİMİZİN HAYATINDA ÖNEMLİ BİR ŞEHİR: MEKKE', 'PH.9.1.2. Cahiliye anlayışını yorumlayabilme', 'Cahiliye dönemine ait Ficar Savaşları ve Hilfülfudul gibi önemli olayların yer aldığı, Hz. Peygamber''in Hilfülfudul''a verdiği önemi, bu topluluğun adalet ve hakkaniyet ilkelerine bağlılığını anlatan bir video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '9', '1. PEYGAMBERİMİZİN HAYATINDA ÖNEMLİ BİR ŞEHİR: MEKKE', 'PH.9.1.3. Hz. Peygamber’in çocukluk yılları konusunu yapılandırabilme', 'Hz. Peygamber’in çocukluk yılları ile ilgili önemli olayların anlatıldığı, görsellerle desteklenen bir video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '9', '1. PEYGAMBERİMİZİN HAYATINDA ÖNEMLİ BİR ŞEHİR: MEKKE', 'PH.9.1.4. Hz. Peygamber’in risalet sonrasında Mekkelilerle kurduğu ilişkileri özetleyebilme', 'Hz. Peygamber’in risalet sonrasında Mekkelilerle kurduğu ilişkileri anlatan video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '9', '2. PEYGAMBERİMİZİN HAYATINDA ÖNEMLİ BİR ŞAHSİYET: HZ. HATİCE', 'PH.9.2.1. Hz. Hatice’nin Hz. Peygamber’le evlilik sürecini yapılandırabilme', 'Hz. Hatice''nin hayatını ve Hz. Peygamber ile olan evliliğini kronolojik olarak gösteren, önemli olayların üzerine tıklanarak detaylı bilgi alınabilen, görsel veya kısa videoların görüntülenebildiği etkileşimli bir zaman çizelgesi hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '9', '2. PEYGAMBERİMİZİN HAYATINDA ÖNEMLİ BİR ŞAHSİYET: HZ. HATİCE', 'PH.9.2.2. Hz. Hatice’nin, Hz. Peygamber’in hayatındaki yeri hakkında sentezleme yapabilme', 'Hz. Hatice''nin hayatını, Hz. Peygamber ile olan ilişkisini ve İslam''ın yayılmasındaki rolünü, Hz. Hatice''nin güçlü karakterini, sadakatini ve fedakârlığını anlatan bir belgesel hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '9', '3. PEYGAMBERİMİZİN HAYATINDA ÖNEMLİ BİR HADİSE: HİCRET', 'PH.9.3.1. Medine’ye hicreti hazırlayan sebepler konusunda genelleme yapabilme', 'Hicretin sebepleri ve sonuçları üzerine tarihçilerin veya din alimlerinin görüşlerini içeren belgesel hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '9', '3. PEYGAMBERİMİZİN HAYATINDA ÖNEMLİ BİR HADİSE: HİCRET', 'PH.9.3.2. Akabe Biatlerini karşılaştırabilme', 'Birinci ve İkinci Akabe Biatları''nın benzerlik ve farklılıklarını karşılaştıran bir video hazırlanır. Hicret güzergahı ile ilgili video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '9', '3. PEYGAMBERİMİZİN HAYATINDA ÖNEMLİ BİR HADİSE: HİCRET', 'PH.9.3.3. Medine’ye hicretin önemini sorgulayabilme', 'Hz. Peygamber''in hicret yolculuğunu ve Medine''ye varışını anlatan, Hz. Peygamber''in ve Hz. Ebu Bekir''in yaşadığı olayları ve Medinelilerin onları karşılama coşkusunu görsellerle anlatan video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '9', '3. PEYGAMBERİMİZİN HAYATINDA ÖNEMLİ BİR HADİSE: HİCRET', 'PH.9.3.4. Hicret’ten sonra devletleşme sürecinde gerçekleşen faaliyetleri sınıflandırabilme', 'Hicret sonrası Medine''de yaşananları, toplumsal düzeni, ekonomik faaliyetleri ve siyasi gelişmeleri anlatan bir belgesel veya animasyon hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '9', '4. EN YÜCE DOSTA', 'PH.9.4.1. Hz. Peygamber’in hastalanmasından vefat etmesine kadar geçen süreçteki or-tamla tarihsel empati kurabilme', 'Veda Haccı''nın gerçekleştiği Mekke ve çevresini, Hz. Peygamber''in veda konuşmasını yaptığı Arafat Dağı''nı, Mina''yı ve diğer önemli yerleri 360 derece sanal tur ile gösteren etkileşimli içerik hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '10', '1. PEYGAMBERİMİZİN HAYATINDA ÖNEMLİ BİR ŞEHİR: TAİF', 'PH.10.1.1. Hz. Peygamber’in Taif yolculuğuna dair tarihsel empati kurabilme', 'Hz. Peygamber’i Taif yolculuğuna sevk eden süreçle ilgili video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '10', '1. PEYGAMBERİMİZİN HAYATINDA ÖNEMLİ BİR ŞEHİR: TAİF', 'PH.10.1.2. Taif’in İslamlaşması olayını sentezleyebilme', 'Taiflilerin İslamlaşma sürecini gösteren etkileşimli zaman çizelgesi hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '10', '2. PEYGAMBERİMİZİN HAYATINDA ÖNEMLİ BİR ŞAHSİYET: HZ. EBU BEKİR', 'PH.10.2.1. Hz. Ebu Bekir’in kişilik özelliklerini yorumlayabilme', 'Hz. Ebu Bekir’in kişisel özellikleri; cömertliği, tevazusu, hilmi, dürüstlüğü, edebi, ilmi, hafızası ve hitabetine vurgu yapılan ses veya video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '10', '2. PEYGAMBERİMİZİN HAYATINDA ÖNEMLİ BİR ŞAHSİYET: HZ. EBU BEKİR', 'PH.10.2.2. Hz. Ebu Bekir’in çocukları ile ilgili bilgi toplayabilme', 'Hz. Ebu Bekir’in ailesi ve çocuklarının isimlerini gösteren bilgi görseli hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '10', '2. PEYGAMBERİMİZİN HAYATINDA ÖNEMLİ BİR ŞAHSİYET: HZ. EBU BEKİR', 'PH.10.2.3. Hz. Ebu Bekir’in tebliğ sürecindeki rolünü özetleyebilme', 'Hz. Ebu Bekir’in Müslüman olması ve İslam davetindeki çabaları kapsamında gizli ve açık davet dönemleri, ilk Müslümanlar, İslam davetine tepkiler, Habeşistan’a hicret, isra ve miraç, Akabe Biatleri konularını ele alan video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '10', '3. PEYGAMBERİMİZİN HAYATINDA ÖNEMLİ BİR HADİSE: BEDİR SAVAŞI', 'PH.10.3.1. Askerî mücadeleyi gerektiren durumlar konusunda genelleme yapabilme', 'Medine’ye hicretten sonra İslam toplumunun oluşma sürecinde gerçekleşen faaliyetleri ele alan ve Mekkeli müşriklerin tutumları ile savaşa izin veren ayetler arasındaki ilişkiyi belirleyen video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '10', '3. PEYGAMBERİMİZİN HAYATINDA ÖNEMLİ BİR HADİSE: BEDİR SAVAŞI', 'PH.10.3.2. Bedir Savaşı’nın taraflarını karşılaştırabilme', 'Bedir Savaşı’nın sebebi ve bu savaş öncesinde her iki ordunun yapmış olduğu hazırlıklar ile Hz. Peygamber’in ashabıyla yaptığı istişarelere değinen bu bağlamda Hz. Peygamber’in istişareye verdiği öneme vurgu yapan video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '10', '3. PEYGAMBERİMİZİN HAYATINDA ÖNEMLİ BİR HADİSE: BEDİR SAVAŞI', 'PH.10.3.3. Bedir Savaşı’nın önemini sorgulayabilme', 'Bedir Savaşı’nın seyri ve sonuçları ile ilgili etkileşimli içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '10', '4. İLAHİ MESAJ', 'PH.10.4.1. Hz. Peygamber’in ilk vahyi alma sürecini çözümleyebilme', 'Hz. Peygamber’in Hira’da geçirdiği süreç, ilk vahiy geldiğindeki ruh hâli ve yaşadıkları ile Hz. Hatice’nin Peygamber Efendimiz’e verdiği desteğe vurgu yapan ses veya video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '11', '1. BİR DAVETÇİ OLARAK PEYGAMBERİMİZ', '2. Peygamberimizin davet ve tebliğindeki temel ilkeleri kavrar.', '* Peygamberimizin davet ve tebliğindeki temel ilkeleriyle ilgili kelime ve cümlelerden oluşturulan eşleştirme, boşluk doldurma, sıralama, doğru yanlış, çoklu seçim, bulmaca, oyun veya benzeri etkileşimli içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '11', '1. BİR DAVETÇİ OLARAK PEYGAMBERİMİZ', '4. Peygamberimizin ahlaki özelliklerinin İslam davetindeki yerini fark eder.', '* Peygamberimizin ahlaki özelliklerinin İslam davetindeki yerini anlatan sunu veya video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '11', '1. BİR DAVETÇİ OLARAK PEYGAMBERİMİZ', '5. İslam davetinde öncü olan sahabilere örnekler verir.', '* İslam davetinde öncü olan sahabileri tanıtan video veya animasyon hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '11', '1. BİR DAVETÇİ OLARAK PEYGAMBERİMİZ', '6. Hz. Hatice’nin kişiliğini ve İslam daveti için gösterdiği fedakârlıkları açıklar.', '* Hz. Hatice’nin kişiliğini ve İslam daveti için gösterdiği fedakârlıkları açıklayan etkileşimli içerik, sunu, video veya animasyon hazırlanır.', 'Video/Sunu/Etkile şimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '11', '1. BİR DAVETÇİ OLARAK PEYGAMBERİMİZ', '7. Peygamberimizin davet metodu ile ilgili bir hadisi yorumlar.', '* Peygamberimizin davet metodu ile ilgili hadise yönelik video, eşleştirme, boşluk doldurma, sıralama, doğru yanlış, çoklu seçim, bulmaca, oyun veya benzeri etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '11', '2. BİR EĞİTİMCİ OLARAK PEYGAMBERİMİZ', '2. Peygamberimizin eğitimdeki temel ilkeleri ve metotlarını örneklerle açıklar.', '* Peygamberimizin eğitimdeki temel ilke ve metotları ile ilgili video, eşleştirme, boşluk doldurma, sıralama, doğru yanlış, çoklu seçim, bulmaca, oyun veya benzeri etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '11', '2. BİR EĞİTİMCİ OLARAK PEYGAMBERİMİZ', '3. Peygamberimizin eğitimde hedef kitlenin özelliklerini dikkate almasına örnekler verir.', '* Peygamberimizin eğitimde hedef kitlenin özelliklerini dikkate almasıyla ilgili video, eşleştirme, boşluk doldurma, sıralama, doğru yanlış, çoklu seçim, bulmaca, oyun veya benzeri etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '11', '2. BİR EĞİTİMCİ OLARAK PEYGAMBERİMİZ', '4. Peygamberimiz dönemindeki eğitim mekânlarını tanır.', '* Peygamberimiz dönemindeki eğitim mekânları ile ilgili infografik, video veya etkileşimli içerik hazırlanır.', 'İnfografik/Video/E tkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '11', '2. BİR EĞİTİMCİ OLARAK PEYGAMBERİMİZ', '5. Hz. Âişe’nin kişiliğini ve ilim öğrenmedeki iştiyakını örneklerle açıklar.', '* Konuyla ilgili video, eşleştirme, boşluk doldurma, sıralama, doğru yanlış, çoklu seçim, bulmaca, oyun veya benzeri etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '11', '2. BİR EĞİTİMCİ OLARAK PEYGAMBERİMİZ', '6. İlim öğrenmenin fazileti ile ilgili bir hadisi yorumlar.', '* İlim öğrenmenin fazileti ile ilgili verilen hadise yönelik video, eşleştirme, boşluk doldurma, sıralama, doğru yanlış, çoklu seçim, bulmaca, oyun veya benzeri etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '11', '3. PEYGAMBERİMİZİN SİYASİ VE ASKERİ YÖNÜ', '1. Medine İslam devletinin genel özelliklerini yorumlar. Uygulamalarını fark eder.', '* Medine İslam devletinin genel özelliklerine yönelik infografik veya video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '11', '3. PEYGAMBERİMİZİN SİYASİ VE ASKERİ YÖNÜ', '3. Peygamberimizin savaş ahlakı ve hukuku ile ilgili evrensel uygulamaları fark eder.', '* Peygamberimizin savaş ahlakı ve hukukuyla ilgili evrensel uygulamalarını anlatan video veya animasyon hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '11', '3. PEYGAMBERİMİZİN SİYASİ VE ASKERİ YÖNÜ', '4. Ebu Ubeyde b. Cerrah’ın kişiliğini ve İslam’a hizmetlerini örneklerle açıklar.', '* Ebu Ubeyde b. Cerrah’ı ve İslam’a hizmetlerini anlatan video veya animasyon hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '11', '3. PEYGAMBERİMİZİN SİYASİ VE ASKERİ YÖNÜ', '5. İdeal bir yöneticide bulunması gereken özellikler ile ilgili bir hadisi yorumlar.', '* Verilen hadise yönelik eşleştirme, boşluk doldurma, sıralama, doğru yanlış, çoklu seçim, bulmaca, oyun veya benzeri etkileşimli içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '11', '4. PEYGAMBERİMİZ DÖNEMİNDE SOSYAL HAYAT', '1. Peygamberimiz döneminde çocukların sosyal hayat içerisindeki konumunu fark eder.', '* Peygamberimizin çocuklar ile ilişkileri, eğitimi, çocuk sevgisi, kız çocuklarına değer vermesi ile ilgili video, animasyon veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '11', '4. PEYGAMBERİMİZ DÖNEMİNDE SOSYAL HAYAT', '2. İslam davetinde gençlerin rolünü kavrar.', '* Peygamberimizin gençler ile ilişkileri, güvenmesi, onlara sorumluluk vermesi ile ilgili video, animasyon veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '11', '4. PEYGAMBERİMİZ DÖNEMİNDE SOSYAL HAYAT', '3. Peygamberimiz döneminde siyasi, sosyal ve ekonomik hayatta kadının yerini yorumlar.', '* Peygamberimizin kadınların toplumdaki yeri ve önemine değer vermesi, eşleri ve kızlarıyla ilişkileri ile ilgili video, eşleştirme, boşluk doldurma, sıralama, doğru yanlış, çoklu seçim, bulmaca, oyun veya benzeri etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '11', '4. PEYGAMBERİMİZ DÖNEMİNDE SOSYAL HAYAT', '4. Asr-ı saâdetteki aile yapısına örnekler verir.', '* Peygamberimizin aileye verdiği değer, aile bireyleri arasında adaletli olması ile ilgili video, animasyon veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '11', '4. PEYGAMBERİMİZ DÖNEMİNDE SOSYAL HAYAT', '5. Peygamberimizin akrabalık ilişkilerine verdiği önemi fark eder.', '* İslam’ın ve Peygamberimizin akraba ilişkileri ile ilgili emir ve tavsiyeleri ile ilgili video, eşleştirme, boşluk doldurma, sıralama, doğru yanlış, çoklu seçim, bulmaca, oyun veya benzeri etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '11', '4. PEYGAMBERİMİZ DÖNEMİNDE SOSYAL HAYAT', '6. Peygamberimizin komşuluk ilişkilerine verdiği önemi örneklerle açıklar.', '* İslam’ın ve Peygamberimizin komşuluk ilişkileri ile ilgili emir ve tavsiyeleri ile ilgili video, animasyon veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '11', '4. PEYGAMBERİMİZ DÖNEMİNDE SOSYAL HAYAT', '7. Asr-ı saâdette oluşturulan İslam kardeşliğinin dayandığı temelleri kavrar.', '* İslam kardeşliği, dayanışma, Ensar ve Muhacir ilişkileri ile ilgili video,  eşleştirme, boşluk doldurma, sıralama, doğru yanlış, çoklu seçim, bulmaca, oyun veya benzeri etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '11', '4. PEYGAMBERİMİZ DÖNEMİNDE SOSYAL HAYAT', '8. Ümmü Seleme’nin örnek şahsiyetini tanır.', '* Peygamberimizin evliliklerinin sebepleri, Ümmü Seleme’nin şahsiyeti ve kabiliyetleri ile ilgili video, eşleştirme, boşluk doldurma, sıralama, doğru yanlış, çoklu seçim, bulmaca, oyun veya benzeri etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '11', '4. PEYGAMBERİMİZ DÖNEMİNDE SOSYAL HAYAT', '9. Müslümanların kardeşliği ile ilgili bir hadisi yorumlar.', '* Müslümanların birbirlerine karşı sorumluluklarını anlatan hadislere yönelik video, animasyon veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '12', '1. PEYGAMBERİMİZE İTAAT', '12.1.1. Peygamberi sevmenin dinî bir sorumluluk olduğunu kavrar.', '* Peygamber sevgisini anlatan şiir, ilahi, şarkıların seslendirildiği video veya animasyon hazırlanır.', 'Video', NULL);
