INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '12', '1. PEYGAMBERİMİZE İTAAT', '12.1.3. İslam’ı anlama ve yaşamada sünnetin yeri ve önemini fark eder.', '* İslam’ı anlama ve yaşamada sünnetin yeri ve önemiyle ilgili eşleştirme, boşluk doldurma, sıralama, doğru yanlış, çoklu seçim, bulmaca, oyun veya benzeri etkileşimli içerik hazırlanır.
