INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '12', '3. PEYGAMBERİMİZ VE TOPLUMSAL DEĞİŞİM', '12.3.1. Cahiliye zihniyeti ile İslam arasında farkı yorumlar.', '* Konuyla ilgili eşleştirme, boşluk doldurma, sıralama, doğru yanlış, çoklu seçim, bulmaca, oyun veya benzeri etkileşimli içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '12', '3. PEYGAMBERİMİZ VE TOPLUMSAL DEĞİŞİM', '12.3.2. Peygamberimizin inanç alanında yaptığı dönüşüme örnekler verir.', '* Peygamberimizin inanç alanında yaptığı dönüşümlerle ilgili video, animasyon, infografik veya sunu hazırlanır.', 'Video/İnfografik/S unu', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '12', '3. PEYGAMBERİMİZ VE TOPLUMSAL DEĞİŞİM', '12.3.3. Peygamberimizin aile hayatında yaptığı dönüşümleri yorumlar.', '* Peygamberimizin aile hayatında yaptığı dönüşümlerle ilgili video, animasyon, infografik veya sunu hazırlanır.', 'Video/İnfografik/S unu', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '12', '3. PEYGAMBERİMİZ VE TOPLUMSAL DEĞİŞİM', '12.3.4. Peygamberimizin ekonomik alanda yaptığı dönüşümleri yorumlar.', '* Peygamberimizin ekonomik alanda yaptığı dönüşümlerle ilgili video, animasyon, infografik veya sunu hazırlanır.', 'Video/İnfografik/S unu', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '12', '3. PEYGAMBERİMİZ VE TOPLUMSAL DEĞİŞİM', '12.3.5. Peygamberimizin siyasi alanda yaptığı dönüşümleri fark eder.', '* Peygamberimizin siyasi alanda yaptığı dönüşümlerle ilgili video, animasyon, infografik veya sunu hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '12', '3. PEYGAMBERİMİZ VE TOPLUMSAL DEĞİŞİM', '12.3.6. Mus’ab b. Umeyr’in İslam davetindeki yerini fark eder.', '* Mus’ab b. Umeyr’in örnek kişiliğinin anlatıldığı etkileşimli içerik, video veya animasyon hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '12', '3. PEYGAMBERİMİZ VE TOPLUMSAL DEĞİŞİM', '12.3.7. İslam ile cahiliye arasındaki farkı anlatan bir hadisi yorumlar.', '* İslam ile cahiliye arasındaki farkı anlatan bir hadisin seslendirildiği ses dosyası hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '12', '4. KÜLTÜRÜMÜZDE PEYGAMBERİMİZ', '12.4.1. Peygamberimizin beşerî özelliklerini açıklar.', '* Peygamberimizin beşerî özelliklerini açıklayan video, animasyon, infografik veya sunu hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '12', '4. KÜLTÜRÜMÜZDE PEYGAMBERİMİZ', '12.4.3. İslam dünyasından Peygamber sevgisine örnekler verir.', '* Peygamber sevgisinin Müslüman kültürlere yansıyışını içeren video veya animasyon hazırlanır.', 'Video', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '12', '4. KÜLTÜRÜMÜZDE PEYGAMBERİMİZ', '12.4.4. Ebu Eyyûb el-Ensari’nin örnek şahsiyetini tanır.', '* Ebu Eyyûb el-Ensari’nin örnek kişiliğinin anlatıldığı video veya animasyon hazırlanır.', 'Video', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Peygamberimizin Hayatı', '12', '4. KÜLTÜRÜMÜZDE PEYGAMBERİMİZ', '12.4.5. Peygamber sevgisi ile ilgili bir hadisi yorumlar.', '* Peygamber sevgisi ile ilgili bir hadisin seslendirildiği ses dosyası veya video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', '9', '1. KENDİMİ, KÂİNATI VE ALLAH’I TANIYORUM', 'TDB.9.1.1. İnsan ve kâinatın yaratılışını, yaratılış özelliklerini ve amacını sorgulayabilme', 'İnsanın ve kâinatın yaratılış özelliklerini ve amaçlarını anlatan video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', '9', '1. KENDİMİ, KÂİNATI VE ALLAH’I TANIYORUM', 'TDB.9.1.3. Kâinattaki işleyiş ve düzenden hareketle Allah’ın (cc) varlığına ve birliğine ulaşmak için akıl yürütme', 'Mülk suresinin 3-4. ayetleri esas alınarak kâinattaki düzeni, işleyişi ve doğal güzellikleri gösteren bir video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', '9', '2. YARATILIŞIN GAYESİ: ALLAH’A
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', '9', '2. YARATILIŞIN GAYESİ: ALLAH’A İMAN', 'TDB.9.2.2. İman esaslarını tespit ederek aralarındaki ilişkiyi çözümleyebilme', 'İman esaslarını anlatan, iman esasları ile ilgili öğretim programında bahsedilen ayet ve hadislerden örnekler veren ve aralarındaki ilişkiyi çözümlemeye yönelik video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', '9', '3. İMANIN MEYVESİ: İBADETLER', 'TDB.9.3.1. İbadet kavramı ve ibadetle ilgili kavramlar arasındaki ilişkiyi çözümleyebilme', 'İbadetle ilgili kavramlar arasındaki ilişkinin çözümlenmesi için kelime ilişkilendirme testi, doğru-yanlış gibi sorularından oluşan etkileşimli içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', '9', '3. İMANIN MEYVESİ: İBADETLER', 'TDB.9.3.2. Abdest, gusül ve teyemmüm arasındaki benzerlikleri ve farklıkları karşılaştırabilme', 'Abdest ve teyemmümün alınışı ile ilgili video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', '9', '3. İMANIN MEYVESİ: İBADETLER', 'TDB.9.3.3. Temel ibadetleri özellikleri ve ilişkileri bakımından sınıflandırabilme', 'Namazın kılınışı ile ilgili video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', '9', '4. İSLAM’IN ÖZÜ: GÜZEL AHLAK', 'TDB.9.4.2. İslam ahlakına göre olumlu ve olumsuz tutumlar ile davranışları sorgulayabilme', 'İslam ahlakına uygun olumlu ve olumsuz tutum ve davranışları anlatan,  çeşitli örnekler içeren video veya etkileşimli video hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', '9', '4. İSLAM’IN ÖZÜ: GÜZEL AHLAK', 'TDB.9.4.3. Kötü alışkanlıklar ve bağımlılıklar hakkında eleştirel düşünebilme', 'Bağımlılığın aile içi ilişkileri nasıl etkilediğini, aile bireyleri arasındaki iletişimi ve güveni nasıl zedelediğini anlatan ve aynı zamanda bağımlılığın milli ve manevi değerlere verdiği zararların vurgulandığı bir video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İMAN VE İNSAN', '1.1.1. İman ve İslam kavramlarını açıklar.', '* İman ve İslam ile ilgili temel kavramlar ve bu kavramların açıklamalarına yönelik eşleştirme, boşluk doldurma, doğru yanlış veya benzeri etkileşimli içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İMAN VE İNSAN', '1.1.2. Kelime-i Tevhid ve Kelime-i Şehadet ile anlamlarını söyler.', '* Kelime-i Tevhid ve Kelime-i Şehadet’in doğru bir kıraatle okunuşuna yönelik ses dosyası veya video hazırlanır.', 'Ses/Video', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İMAN VE İNSAN', '1.1.4. İman bakımından insanların sınıflandırılmasına örnekler verir.', '* Mümin, kâfir ve münafık, müşrik ve ehl- i kitap kavramlarıyla ilgili eşleştirme, boşluk doldurma, sıralama, doğru yanlış, çoklu seçim, bulmaca, oyun veya benzeri etkileşimli içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İMAN VE İNSAN', '1.1.5. Esmâ-i Hüsnâ’dan Allah (c.c.), el-Vâhid, el-Ehad, el-Mü’min isimlerini tanır.', '* Konuyla ilgili eşleştirme, boşluk doldurma, sıralama, doğru yanlış, çoklu seçim, bulmaca, oyun veya benzeri etkileşimli içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İBADET VE İNSAN', '1.2.2. İbadetin kabul şartlarını sayar.', '* İbadetin şekli, sayısı, zamanı ve miktarı Şari’ (Allah ve Resulü) tarafından belirlenmesiyle ilgili infografik, video veya etkileşimli içerik hazırlanır.', 'İnfografik/Video/E tkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İBADET VE İNSAN', '1.2.3. İbadetlerle salih amelleri ilişkilendirir.', '* Salih amellerle ilgili video, animasyon veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İBADET VE İNSAN', '1.2.5. Esmâ-i Hüsnâ’dan es- Samed, er-Rakîb, el-Vedûd isimlerini tanır.', '* Konuyla ilgili video, eşleştirme, boşluk doldurma, sıralama, doğru yanlış, çoklu seçim, bulmaca, oyun veya benzeri etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'MÜMİNLERİN ÖZELLİKLERİ', '1.3.1. Ayet ve hadislerden hareketle doğrulukla ilgili çıkarımlarda bulunur.', '* Asr-ı saadetten ve tarihimizden örnek olaylardan doğrulukla ilgili video, animasyon veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'MÜMİNLERİN ÖZELLİKLERİ', '1.3.3. Müminlerin iyiliği emretmek, kötülüğü engellemekle ilgili sorumluluklarını değerlendirir.', '* İyiliği emretme ve kötülüğü engellemenin bireysel ve toplumsal yönü ile kamu otoritesini ilgilendiren alanlarıyla ilgili sunu, video veya etkileşimli içerik hazırlanır.', 'Video/Sunu/Etkile şimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'MÜMİNLERİN ÖZELLİKLERİ', '1.3.4. Yoksulun hakkını gözetmenin önemli bir dinî görev olduğunu savunur.', '* Yoksulun hakkını gözetme konusunda tarihî ve güncel olaylardan örneklerle ilgili video veya animasyon hazırlanır.', 'Video', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'MÜMİNLERİN ÖZELLİKLERİ', '1.3.5. İsraf ve cimriliğin önemli birer ahlaki zafiyet olduğunu fark eder.', '* İsraf ve cimriliğin zararlarıyla ilgili video veya animasyon hazırlanır.', 'Video', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'MÜMİNLERİN ÖZELLİKLERİ', '1.3.6. Esmâ-i Hüsnâ’dan el-Bâsit, el- Muhsin, el-Mani’ isimlerini tanır.', '* Esmâ-i Hüsnâ’dan el-Bâsit, el- Muhsin, el-Mani’ isimleriyle ilgili hat ve tezhip örneklerinin bulunduğu görseller ile isimlerin eşleştirilmesine yönelik etkileşimli içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'NEZAKET', '1.4.1. Nezaketin kişinin Müslümanlığının önemli tezahürü olduğunu fark eder.', '* Nezaket kurallarıyla ilgili örneklerden sunu, video veya etkileşimli içerik hazırlanır.', 'Video/Sunu/Etkile şimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'NEZAKET', '1.4.2. Kur’an-ı Kerim’den nezaket örneklerini sıralar.', '* Nezaket kuralları ile ayetleri eşleştirme veya benzeri etkileşimli içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'NEZAKET', '1.4.4. Geleneğimizde yer alan nezaket örneklerini tanır.', '* İslam büyükleri, tarihî şahsiyetler, âlim ve edipler gibi geleneğimizin oluşmasında önemli rolü olan kişilerin nezaketle ilgili örnekleri içeren sunu, video veya etkileşimli içerik hazırlanır.', 'Video/Sunu/Etkile şimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'NEZAKET', '1.4.6. Esmâ-i Hüsnâ’dan el-Halîm, el- Latîf, er- Refîk isimlerini tanır.', '* Esmâ-i Hüsnâ’dan el-Halîm, el-Latîf, er- Refîk isimleriyle ilgili hat ve tezhip örneklerinin bulunduğu görseller ile isimlerin eşleştirilmesine yönelik etkileşimli içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İSLAM VE AHLAK', '2.1.1. Allah’a (c.c.) karşı ahlaki sorumluluklarını yerine getirmeye özen gösterir.', '* Konuyla ilgili ayet ve hadislerin yer aldığı, konuyu özetleyen sunu veya video hazırlanır.', 'Video/Sunu', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İSLAM VE AHLAK', '2.1.2. Peygamberimize (s.a.v.) karşı ahlaki sorumluluklarını yerine getirmede istekli olur.', '* Salavatın Türkçe anlamının da bulunduğu doğru bir kıraatle okunuş videosu hazırlanır.', 'Video', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İSLAM VE AHLAK', '2.1.4. Topluma karşı ahlaki sorumluluklarını yerine getirmeye duyarlı olur.', '* Adalet, hikmet, şecaat (cesaret) iffet ve yardımlaşma temalı topluma karşı sorumlulukları yansıtacak kısa film, video, animasyon veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İSLAM VE AHLAK', '2.1.5. Esmâ-i Hüsnâ’dan el- Azîz, er- Rahman, es-Selam isimlerini tanır.', '* el- Azîz, er-Rahman, es-Selam isimleriyle ilgili hat ve tezhip örneklerinin bulunduğu görseller ile isimlerin eşleştirilmesine yönelik etkileşimli içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İSLAM VE TEMİZLİK', '2.2.1. İslam’ın temizliğe verdiği önemi ayet ve hadislerle açıklar.', '* İslam’ın temizliğe verdiği önemi ayet ve hadislerden örneklerle anlatan video, animasyon veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İSLAM VE TEMİZLİK', '2.2.2. Temizlik çeşitlerini sıralar.', '* Temizlik çeşitlerini sunan infografik veya etkileşimli içerik hazırlanır.', 'İnfografik/Etkileşi mli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İSLAM VE TEMİZLİK', '2.2.3. Gusül, abdest, teyemmüm ile ilgili hükümleri açıklar.', '* Abdestin nasıl alındığını gösteren video ya da animasyon hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İSLAM VE TEMİZLİK', '2.2.4. Esmâ-i Hüsnâ’dan el-Kerim, el- Kuddûs, et-Tevvâb isimlerini tanır.', '* el-Kerim, el-Kuddûs, et-Tevvâb isimleriyle ilgili hat ve tezhip örneklerinin bulunduğu görseller ile isimlerin eşleştirilmesine yönelik etkileşimli içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İSLAM’IN DİREĞİ NAMAZ', '2.3.1. Namazla ilgili hükümleri açıklar.', '* Namazın farzları, vacipleri ve sünnetleri ile namazı bozan durumlarla ilgili infografik veya etkileşimli içerik hazırlanır.', 'İnfografik/Etkileşi mli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İSLAM’IN DİREĞİ NAMAZ', '2.3.2. Sehiv ve tilavet secdesiyle ilgili hükümleri söyler.', '* Sehiv secdesinin yapılışını anlatıldığı/ gösterildiği video veya animasyon hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İSLAM’IN DİREĞİ NAMAZ', '2.3.3. Namazda okunan dua ve tesbihleri doğru olarak telaffuz eder.', '* Namazda okunan dua ve teşbihlerin Türkçe anlamının da bulunduğu doğru bir kıraatle okunuşlarının videosu veya ses dosyası hazırlanır.', 'Video/Ses', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İSLAM’IN DİREĞİ NAMAZ', '2.3.4. Mescitlerin özellik ve fonksiyonlarını yorumlar.', '* Mescid-i Haram, Mescid-i Nebi ve Mescid-i Aksâ’nın tanıtıldığı, Müslümanlar için önemini anlatan video hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İSLAM’IN DİREĞİ NAMAZ', '2.3.5. Esmâ-i Hüsnâ’dan er-Rab, el- Mucîb, el-Ğafûr, el- Câmi’ isimlerini tanır.', '* er-Rab, el-Mucîb, el-Ğafûr, el-Câmi’ isimleriyle ilgili hat ve tezhip örneklerinin bulunduğu görseller ile isimlerin eşleştirilmesine yönelik etkileşimli içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İSLAM VE GÜNLÜK HAYAT', '2.4.1. Edep ve hayânın İslam ahlakındaki yerini örneklerle açıklar.', '* Konuyla ilgili eşleştirme, boşluk doldurma, sıralama, doğru yanlış, çoklu seçim, bulmaca, oyun veya benzeri etkileşimli içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İSLAM VE GÜNLÜK HAYAT', '2.4.2. Dostluk ve arkadaşlığın önemiyle ilgili tarihî ve güncel olaylardan örnekler verir.', '* Dostluk ve arkadaşlığın önemiyle ilgili tarihî ve güncel olaylardan örneklerin anlatıldığı video veya animasyon hazırlanır.', 'Video', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İSLAM VE GÜNLÜK HAYAT', '2.4.3. Allah’ın nimetlerine şükretmenin önemli bir ahlaki görev olduğunun farkına varır.', '* Allah’ın (c.c.) verdiği nimetlerden örneklerin görsellerle desteklenerek anlatıldığı sunu, video veya etkileşimli içerik hazırlanır.', 'Video/Sunu/Etkile şimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İSLAM VE GÜNLÜK HAYAT', '2.4.4. Kul hakkına riayet etmeye istekli olur.', '* Konuyla ilgili eşleştirme, boşluk doldurma, sıralama, doğru yanlış, çoklu seçim, bulmaca, oyun veya benzeri etkileşimli içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İSLAM VE GÜNLÜK HAYAT', '2.4.5. Esmâ-i Hüsnâ’dan el-Afüv, eş- Şekûr, er-Raûf isimlerini tanır.', '* el-Afüv, eş-Şekûr, er-Raûf isimleriyle ilgili hat ve tezhip örneklerinin bulunduğu görseller ile isimlerin eşleştirilmesine yönelik etkileşimli içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İSLAM VE SOSYAL HAYAT', '1. İslam’ın aileye verdiği önemi kavrar.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İSLAM VE SOSYAL HAYAT', '3. İslam’ın sosyal hayatla ilgili bazı temel ilkelerini ayet ve hadislerle açıklar.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İSLAM VE SOSYAL HAYAT', '5. Sosyal hayatı olumsuz etkileyen unsurları fark eder.', '* Sosyal hayatı olumsuz etkileyen bazı unsurlar ve bunların topluma verdiği zararlar ile ilgili sunu, video, animasyon veya etkileşimli içerik hazırlanır.', 'Video/Sunu/Etkile şimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İSLAM VE EKONOMİK HAYAT', '1 İslam ekonomisinin temelindeki bazı ahlaki ilkeleri açıklar.', '* İslam ekonomisinin temelindeki bazı ahlaki ilkeler ile ilgili sunu, video, animasyon veya etkileşimli içerik hazırlanır.', 'Video/Sunu/Etkile şimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İSLAM VE EKONOMİK HAYAT', '3. İslam tarihinden infak kültürüyle ilgili örnekler verir.', '* Sahabe yaşantılarından örnek bir hikâye anlatan video, animasyon veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İSLAM VE EKONOMİK HAYAT', '4. Karz-ı haseni ayet ve hadislerle açıklar.', '* Karz-ı hasen ile ilgili ayet ve hadislerden oluşan sunu, video veya etkileşimli içerik hazırlanır.', 'Video/Sunu/Etkile şimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İSLAM VE EKONOMİK HAYAT', '5. Kul hakkına riayet etmeye özen gösterir.', '* Kul hakkına riayet etmenin önemi ile ilgili video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İSLAM VE EKONOMİK HAYAT', '6. İslam’ın işçi ve işveren hakkına verdiği önemi kavrar.', '* İslam’ın işçi ve işveren hakkına verdiği önem ile ilgili video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İSLAM VE EKONOMİK HAYAT', '8. Ekonomik hayatı tehdit eden olumsuzlukları İslam’ın bakış açısına göre değerlendirir.', '* Ekonomik hayatı olumsuz etkileyen bazı unsurlar ve bunların topluma verdiği zararlar ile ilgili sunu, video, animasyon veya etkileşimli içerik hazırlanır.', 'Video/Sunu/Etkile şimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İSLAM VE HUKUK', '1. İslam hukukunu ve mahiyetini açıklar.', '* Konuyla ilgili eşleştirme, boşluk doldurma, sıralama, doğru yanlış, çoklu seçim, bulmaca, oyun veya benzeri etkileşimli içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İSLAM VE HUKUK', '2. İslam hukukunun temel ilkelerine ayet ve hadislerden örnek verir.', '* İslam hukukunun temel ilkeleri ile ilgili ayet ve hadislere yönelik sunu, video veya etkileşimli içerik hazırlanır.', 'Video/Sunu/Etkile şimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İSLAM VE HUKUK', '3. İslam hukukunun kaynaklarını açıklar.', '* İslam   hukukunun   kaynakları   ile   ilgili eşleştirme,  boşluk  doldurma,  sıralama, doğru   yanlış, çoklu   seçim,   bulmaca, oyun   veya   benzeri   etkileşimli   içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'ANA HATLARIYLA İSLAM AHLAKI', '1. İslam ahlakının gayesini ve konusunu ayet ve hadislerle açıklar.', '* İslam ahlakının gayesi ile ilgili ayet ve hadislerin açıklandığı video, animasyon veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'ANA HATLARIYLA İSLAM AHLAKI', '2. İslam ahlakının kaynaklarını açıklar.', '* Konuyla ilgili infografik, video veya etkileşimli içerik hazırlanır.', 'İnfografik/Video/E tkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'ANA HATLARIYLA İSLAM AHLAKI', '3. Ahlak ile terbiye ilişkisini yorumlar.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'ANA HATLARIYLA İSLAM AHLAKI', '5. Allah’a karşı vazifelerini yerine getirmeye özen gösterir.', '* Allah’a karşı vazifeler ile ilgili eşleştirme, boşluk doldurma, sıralama, doğru yanlış, çoklu seçim, bulmaca, oyun veya benzeri etkileşimli içerik hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'ANA HATLARIYLA İSLAM AHLAKI', '6. Peygamberimize karşı vazifelerini yerine getirmeye istekli olur.', '* Peygamberimize karşı vazifeler ile ilgili infografik, video veya etkileşimli içerik hazırlanır.', 'İnfografik/Video/E tkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'ANA HATLARIYLA İSLAM AHLAKI', '7. Kur’an-ı Kerim ile ilgili vazifelerini yapmaya önem verir.', '* Kur’an-ı Kerim ile ilgili vazifeleri anlatan video, animasyon veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'ANA HATLARIYLA İSLAM AHLAKI', '8. İnsanlara karşı vazifelerini kavrar.', '* İnsanlara karşı vazifeler ile ilgili infografik, video veya etkileşimli içerik hazırlanır.', 'İnfografik/Video/E tkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İMAN', '1.1. İman ve İslam arasındaki ilişkiyi açıklar.', '* İman ve İslam kavramları arasındaki ilişkiyi ve farklılıkları gösteren infografik, video veya etkileşimli içerik hazırlanır.', 'İnfografik/Video/E tkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İMAN', '1.2. İslam inancının temel kaynaklarını açıklar.', '* İslam inancının temel kaynakları olan kitap ve sünnet hakkında sunu, video veya etkileşimli içerik hazırlanır.', 'Sunu/Video/Etkile şimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İMAN', '1.3. İmanın mahiyetini açıklar.', '* İcmali ve tafsili iman konularına yönelik video veya konuyla ilgili etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İMAN', '1.4. İmanın geçerli olması için gerekli şartları açıklar.', '* İmanın geçerli olması için gerekli şartlara yönelik sunu, video veya etkileşimli içerik hazırlanır.', 'Sunu/Video/Etkile şimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İMAN', '1.5. İnanç bakımından insanları sınıflandırır.', '* Ayet ve hadislerde yer alan mümin, münafık ve kâfir kişilere ait özelliklerin söz konusu kavramlar ile doğru eşleştirebilmesini hedefleyen oyun hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'İMAN', '1.6. İslam inanç esaslarını bütüncül olarak kavrar.', '* İslam inanç esaslarına yönelik video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'NAMAZ', '2.1.İbadetlerin yapılış amacını kavrar.', '* İbadet kavramını ve ibadetlerin yapılış amacına yönelik sunu, video veya etkileşimli içerik hazırlanır.', 'Sunu/Video/Etkile şimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'NAMAZ', '2.1. İbadetlerin geçerli olma şartlarını açıklar.', '* İbadetlerin geçerli olma şartlarından niyet, ihlas, sünnete uygunluk kavramlarını açıklayan infografik, video veya etkileşimli içerik hazırlanır.', 'İnfografik/Video/E tkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'NAMAZ', '2.1.3.İbadetlerin bireysel ve toplumsal faydalarının farkında olur.', '* İbadetlerin bireysel ve toplumsal faydalarını listeleyen infografik, video veya etkileşimli içerik hazırlanır.', 'İnfografik/Video/E tkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'NAMAZ', '2.1.4. Hz. Peygamber’in ibadet hayatındaki ilkeleri örneklerle açıklar.', '* Hz. Peygamber’in hayatından ibadet ilkeleriyle ilgili örnek bir olayın anlatıldığı video veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'NAMAZ', '2. 5. Namazın önemini ayet ve hadislerle açıklar.', '* Farklı konularda verilen ayet ve hadisler içinden namazın önemine işaret eden ayet ve hadislerin bulunmasını hedefleyen oyun hazırlanır.', 'Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'NAMAZ', '2. 6. Namazın Allah’a kulluğun en önemli göstergelerinden biri olduğunu fark eder.', '* Namaz ibadeti ve Allah’a kulluk arasındaki ilişkiye yönelik sunu, video veya etkileşimli içerik hazırlanır.', 'Sunu/Video/Etkile şimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'NAMAZ', '2. 7. Namazların kılınışını gösterir.', '* Beş vakit namaz, cemaatle namaz, cuma namazı, bayram namazı, cenaze namazı, kaza namazları, nafile namazların kılınışını gösteren video, animasyon veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'ORUÇ VE ZEKÂT', '3.1 Ramazan ayı ile orucun, Müslümanların hayatındaki yerini ve önemini kavrar.', '* Orucun sabır ve irade eğitimi olduğunu, Kur’an-ı Kerim’in Ramazan ayında indirildiğini, orucun önemini anlatan sunu, video veya konuyla ilgili etkileşimli içerik hazırlanır.', 'Sunu/Video/Etkile şimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'ORUÇ VE ZEKÂT', '3.2. Oruçluyken dikkat edilmesi gereken hususları kavrar.', '* Oruçluyken dikkat edilmesi gereken hususları anlatan video, animasyon veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'ORUÇ VE ZEKÂT', '3.3. Oruçla ilgili hükümleri açıklar.', '* Orucun kazası ve kefareti ile ilgili hükümleri açıklayan sunu, video veya konuyla ilgili etkileşimli içerik hazırlanır.', 'Sunu/Video/Etkile şimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'ORUÇ VE ZEKÂT', '3.4. Zekât ibadetini ayet ve hadislerle açıklar.', '* Zekâtın önemiyle ilgili ayet ve hadislerin yer aldığı, zekâtın malı bereketlendireceğini anlatan sunu, video veya konuyla ilgili etkileşimli içerik hazırlanır.', 'Sunu/Video/Etkile şimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'ORUÇ VE ZEKÂT', '3.5. Zekâtla ilgili hükümleri açıklar.', '* Zekâta tabi mallara ilişkin hükümleri açıklayan sunu, video veya animasyon hazırlanır.', 'Video/Sunu', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'ORUÇ VE ZEKÂT', '3.6. Zekât ibadetinin bireysel ve toplumsal faydalarını kavrar.', '* Zekâtın, bireysel açıdan insanın gereksinimi, sosyolojik olarak ise fakirlik için çözüm yollarından biri olduğunu anlatan sunu, video, animasyon veya konuyla ilgili etkileşimli içerik hazırlanır.', 'Video/Sunu/Etkile şimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'ORUÇ VE ZEKÂT', '3.7. İnfak ve sadakanın yardımlaşma ve dayanışmaya katkısını fark eder.', '* İnfak ve sadakanın yardımlaşma ve dayanışmaya katkısını anlatan video, animasyon veya konuyla ilgili etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'HAC VE KURBAN', '4.1. Kâbe ile hac arasındaki ilişkiyi açıklar.', '* Kâbe’nin tarihinden başlayarak Kâbe ve hac arasındaki ilişkiyi açıklayan video, animasyon veya konuyla ilgili etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'HAC VE KURBAN', '4.2. Haccın dindeki yerini ayet ve hadislerle açıklar.', '* Haccın konu edildiği ayet ve hadisleri içeren sunu, video veya konuyla ilgili etkileşimli içerik hazırlanır.', 'Sunu/Video/Etkile şimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'HAC VE KURBAN', '4.3. Hac ile ilgili kavramları ve hükümleri açıklar.', '* Hac ile ilgili kavramları ve hükümleri açıklayan infografik, video veya etkileşimli içerik hazırlanır.', 'İnfografik/Video/E tkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'HAC VE KURBAN', '4. 4.Haccın yapılışını açıklar.', '* Hac ibadetinin yerine getirirken yapılması gerekenleri anlatan sunu, video, animasyon veya etkileşimli içerik hazırlanır.', 'Video/Sunu/Etkile şimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'HAC VE KURBAN', '4.5. Hac ibadetinin birey ve toplum hayatı üzerindeki etkilerini kavrar.', '* Hac ibadetinin birey ve toplum hayatı üzerindeki etkilerini anlatan sunu, video, animasyon veya etkileşimli içerik hazırlanır.', 'Video/Sunu/Etkile şimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'HAC VE KURBAN', '4.6. Umre ile hac arasındaki farkı ayırt eder.', '* Umrenin yapılışını gösteren video veya animasyon hazırlanır.
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'HAC VE KURBAN', '4.7. Kurban ile ilgili hükümleri açıklar.', '* Kurban, kurban ile ilgili kavramları ve hükümleri açıklayan sunu, video, animasyon veya etkileşimli içerik hazırlanır.', 'Video/Sunu/Etkile şimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'HAC VE KURBAN', '4.8. Kurban ibadetini ve yapılış amacını kavrar.', '* Kurban ibadetini ve yapılış amacını, kurban ibadetinin bireysel ve toplumsal faydalarını anlatan sunu, video, animasyon veya etkileşimli içerik hazırlanır.', 'Video/Sunu/Etkile şimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Temel Dini Bilgiler', NULL, 'HAC VE KURBAN', '4.9. Cihat ve cihatla ilgili kavramları ayet ve hadislerle açıklar.', '* Cihat ve cihatla ilgili davet, tebliğ, emr-i bi’l-maruf nehy-i ani’l-münker kavramlarını açıklayan, cihadın vatan savunmasındaki önemini anlatan video, animasyon veya etkileşimli içerik hazırlanır.', 'Video/Etkileşimli İçerik', NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Bilim Sanat', NULL, NULL, 'Algoritma temelli yaklaşımlarla problem çözebilme', NULL, NULL, NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Bilim Sanat', NULL, NULL, 'Matematiksel oyunlarda bulunan algoritmik yapılara dair muhakeme yapabilme', NULL, NULL, NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Bilim Sanat', NULL, NULL, 'Temel geometrik şekillerin inşası için uygun matematiksel araçlarından yararlanabilme', NULL, NULL, NULL);
INSERT INTO icerik_kayitlari (ders_adi, sinif, unite, kazanim, aciklama, icerik_turu, diger_aciklama) VALUES ('Bilim Sanat', NULL, NULL, 'Günümüzde kullanılan sayı sistemlerinin özelliklerinden(işlemler, basamak isimleri vb.) yola çıkarak farklı sayı sistemlerinin özelliklerine dair analojik akıl yürütebilme', NULL, NULL, NULL);
